        await db
          .update(messageRequests)
          .set({ status: 'accepted' })
          .where(eq(messageRequests.id, requestId));

        res.json({ message: 'Message request accepted', chatId: newChat.id });
      } else {
        // Update request status to rejected
        await db
          .update(messageRequests)
          .set({ status: 'rejected' })
          .where(eq(messageRequests.id, requestId));

