    } catch (error) {
      console.error('Error getting friend requests:', error);
      return [];
    }
  }

  async acceptFriendRequest(requestId: number, userId: number): Promise<boolean> {
    try {
      console.log('Accepting friend request:', requestId, 'by user:', userId);

      const request = this.friendRequests.get(requestId);
      if (!request || request.toUserId !== userId || request.status !== 'pending') {
        console.error('Invalid friend request:', { request, userId });
        return false;
      }

      // Check if chat already exists between these users
      const existingChat = Array.from(this.chats.values()).find(chat => 
        (chat.user1Id === request.fromUserId && chat.user2Id === request.toUserId) ||
        (chat.user1Id === request.toUserId && chat.user2Id === request.fromUserId)
      );

      if (!existingChat) {
        // Create chat between users
        const chat = {
          id: this.currentChatId++,
          user1Id: Math.min(request.fromUserId, request.toUserId),
          user2Id: Math.max(request.fromUserId, request.toUserId),
          createdAt: new Date(),
          messages: []
        };

        this.chats.set(chat.id, chat);
        console.log('Chat created with ID:', chat.id);
      }

      // Create mutual follow relationship
      const follow1 = {
        id: this.currentFollowId++,
        followerId: request.fromUserId,
        followingId: request.toUserId,
        createdAt: new Date()
      };

      const follow2 = {
        id: this.currentFollowId++,
        followerId: request.toUserId,
        followingId: request.fromUserId,
        createdAt: new Date()
      };

      this.follows.set(follow1.id, follow1);
      this.follows.set(follow2.id, follow2);

      // Update request status
      request.status = 'accepted';
      this.friendRequests.set(requestId, request);

      console.log('Friend request accepted successfully');
      console.log('Total chats:', this.chats.size);
      console.log('Total follows:', this.follows.size);

      return true;
    } catch (error) {
      console.error('Error accepting friend request:', error);
      return false;
    }
  }

  async rejectFriendRequest(requestId: number, userId: number): Promise<boolean> {
    try {
      const request = this.friendRequests.get(requestId);
      if (!request || request.toUserId !== userId || request.status !== 'pending') {
        return false;
      }

      // Update request status
      request.status = 'rejected';
      this.friendRequests.set(requestId, request);

      return true;
    } catch (error) {
      console.error('Error rejecting friend request:', error);
      return false;
    }
  }

  async removeFriend(userId: number, friendId: number): Promise<boolean> {
    try {
      // Remove mutual follow relationships
      const followsToRemove: number[] = [];
      for (const [id, follow] of this.follows) {
        if ((follow.followerId === userId && follow.followingId === friendId) ||
            (follow.followerId === friendId && follow.followingId === userId)) {
          followsToRemove.push(id);
        }
      }

      followsToRemove.forEach(id => this.follows.delete(id));

      // Remove chat between users
      const chatsToRemove: number[] = [];
      for (const [id, chat] of this.chats) {
        if ((chat.user1Id === userId && chat.user2Id === friendId) ||
            (chat.user1Id === friendId && chat.user2Id === userId)) {
          chatsToRemove.push(id);
        }
      }

      chatsToRemove.forEach(id => this.chats.delete(id));

      return true;
    } catch (error) {
      console.error('Error removing friend:', error);
      return false;
    }
  }

  async getUserChats(userId: number): Promise<any[]> {
