  async sendMessage(chatId: number, senderId: number, message: string): Promise<any> {
    try {
      const chat = this.chats.get(chatId);
      if (!chat) {
        console.error('Chat not found with ID:', chatId);
        console.log('Available chats:', Array.from(this.chats.keys()));
        throw new Error('Chat not found');
      }

      const newMessage = {
        id: Date.now() + Math.random(), // Better unique ID generation
        senderId,
        message,
        createdAt: new Date()
      };

      // Ensure messages array exists
      if (!chat.messages) {
        chat.messages = [];
      }

      chat.messages.push(newMessage);

      // Force update the chat in the map
      this.chats.set(chatId, { ...chat });

      console.log('Message sent successfully:', newMessage);
      console.log('Chat now has', chat.messages.length, 'messages');

      return newMessage;
    } catch (error) {
      console.error('Error sending message:', error);
      throw error;
    }
  }

