  async followUser(followerId: number, followingId: number): Promise<boolean> {
    try {
      // Check if already following to prevent duplicates
      const existing = await db
        .select()
        .from(follows)
        .where(and(eq(follows.followerId, followerId), eq(follows.followingId, followingId)))
        .limit(1);

      if (existing.length > 0) {
        return false; // Already following
      }

      await db
        .insert(follows)
        .values({ followerId, followingId });

      // Update follower counts accurately by counting actual follows (excluding admin users)
      const followerCount = await db
        .select({ count: sql<number>`count(*)` })
        .from(follows)
        .innerJoin(users, eq(follows.followerId, users.id))
        .where(and(eq(follows.followingId, followingId), eq(users.isAdmin, false)));

      const followingCount = await db
        .select({ count: sql<number>`count(*)` })
        .from(follows)
        .innerJoin(users, eq(follows.followingId, users.id))
        .where(and(eq(follows.followerId, followerId), eq(users.isAdmin, false)));

      await db
        .update(users)
        .set({ followersCount: followerCount[0].count })
        .where(eq(users.id, followingId));

      await db
        .update(users)
        .set({ followingCount: followingCount[0].count })
        .where(eq(users.id, followerId));

      // Create notification for followed user
      const [follower] = await db
        .select()
        .from(users)
        .where(eq(users.id, followerId));

      if (follower) {
        await this.createNotification(
          followingId,
          'follow',
          `${follower.username} started following you`,
          followerId
        );
      }

      return true;
    } catch (error) {
      console.error('Error following user:', error);
      return false;
    }
  }

  async unfollowUser(followerId: number, followingId: number): Promise<boolean> {
    try {
      const result = await db
