  async sendFriendRequest(fromUserId: number, toUserId: number): Promise<boolean> {
    try {
      // Prevent self-friend requests
      if (fromUserId === toUserId) {
        return false;
      }

      // Get both users to check admin status
      const [fromUser] = await db.select().from(users).where(eq(users.id, fromUserId));
      const [toUser] = await db.select().from(users).where(eq(users.id, toUserId));

      if (!fromUser || !toUser) {
        return false;
      }

      // Only prevent friend requests between regular users and system admins
      // Allow friend requests between all regular users
      if (!fromUser?.isAdmin && toUser?.isAdmin) {
        return false; // Block friend request to admin
      }

      // Check if request already exists
      const existingRequest = await db
        .select()
        .from(friendRequests)
        .where(and(
          eq(friendRequests.fromUserId, fromUserId),
          eq(friendRequests.toUserId, toUserId)
        ));

      if (existingRequest.length > 0) {
        return false; // Request already exists
      }

      await db.insert(friendRequests).values({
        fromUserId,
        toUserId,
        status: 'pending',
        createdAt: new Date()
      });

      console.log(`Friend request created: ${fromUserId} -> ${toUserId}`);
      return true;
    } catch (error) {
      console.error('Error sending friend request:', error);
      return false;
    }
  }

  async getFriendRequests(userId: number): Promise<any[]> {
    try {
      const requests = await db
        .select()
        .from(friendRequests)
        .where(and(
          eq(friendRequests.toUserId, userId),
          eq(friendRequests.status, 'pending')
        ));

      // Get user details for each request and filter out invalid users
      const requestsWithUsers = [];
      for (const req of requests) {
        const fromUser = await db
          .select()
          .from(users)
          .where(eq(users.id, req.fromUserId))
          .limit(1);

        // Only include requests from users that exist, have valid data, and are not admin users
        if (fromUser[0] && fromUser[0].username && fromUser[0].displayName !== undefined && !fromUser[0].isAdmin) {
          requestsWithUsers.push({
            id: req.id,
            fromUserId: req.fromUserId,
            toUserId: req.toUserId,
            status: req.status,
            createdAt: req.createdAt,
            user: fromUser[0],
          });
        } else {
          // Clean up invalid or admin friend request
          await db
            .delete(friendRequests)
            .where(eq(friendRequests.id, req.id));
          console.log('Removed invalid or admin friend request from user:', req.fromUserId);
        }
      }

      return requestsWithUsers;
    } catch (error) {
      console.error('Error getting friend requests:', error);
      return [];
    }
  }

  async acceptFriendRequest(requestId: number, userId: number): Promise<boolean> {
    try {
      // Get the friend request
      const [request] = await db
        .select()
        .from(friendRequests)
        .where(and(
          eq(friendRequests.id, requestId),
          eq(friendRequests.toUserId, userId)
        ));

      if (!request) {
        return false;
      }

      // Get both users to check admin status
      const [fromUser] = await db.select().from(users).where(eq(users.id, request.fromUserId));
      const [toUser] = await db.select().from(users).where(eq(users.id, request.toUserId));

      // Prevent non-admin users from connecting with admin users
      if ((fromUser?.isAdmin && !toUser?.isAdmin) || (!fromUser?.isAdmin && toUser?.isAdmin)) {
        // Delete the friend request instead of accepting it
        await db
          .delete(friendRequests)
          .where(eq(friendRequests.id, requestId));
        return false;
      }

      // Create mutual follow relationship
      await db.insert(follows).values([
        {
          followerId: request.fromUserId,
          followingId: request.toUserId,
          createdAt: new Date()
        },
        {
          followerId: request.toUserId,
          followingId: request.fromUserId,
          createdAt: new Date()
        }
      ]);

      // Create chat for the two users
      await db.insert(chats).values({
        user1Id: Math.min(request.fromUserId, request.toUserId),
        user2Id: Math.max(request.fromUserId, request.toUserId),
        createdAt: new Date()
      });

      // Update request status
      await db
        .update(friendRequests)
        .set({ status: 'accepted' })
        .where(eq(friendRequests.id, requestId));

      return true;
    } catch (error) {
      console.error('Error accepting friend request:', error);
      return false;
    }
  }

  async rejectFriendRequest(requestId: number, userId: number): Promise<boolean> {
