  async removeFriend(userId: number, friendId: number): Promise<boolean> {
    try {
      // Remove mutual follow relationships
      await db.delete(follows).where(
        or(
          and(eq(follows.followerId, userId), eq(follows.followingId, friendId)),
          and(eq(follows.followerId, friendId), eq(follows.followingId, userId))
        )
      );

      // Remove chat between users
      await db.delete(chats).where(
        or(
          and(eq(chats.user1Id, Math.min(userId, friendId)), eq(chats.user2Id, Math.max(userId, friendId))),
          and(eq(chats.user1Id, Math.max(userId, friendId)), eq(chats.user2Id, Math.min(userId, friendId)))
        )
      );

      return true;
    } catch (error) {
      console.error('Error removing friend:', error);
      return false;
    }
  }

  async getUserChats(userId: number): Promise<any[]> {
    try {
      // Get chats where both users are following each other (mutual friendship)
      const userChats = await db
        .select({
          id: chats.id,
          createdAt: chats.createdAt,
          user1Id: chats.user1Id,
          user2Id: chats.user2Id
        })
        .from(chats)
        .where(or(
          eq(chats.user1Id, userId),
          eq(chats.user2Id, userId)
        ));

      // Filter chats to include mutual friends OR admin chats
      const validChats = [];
      for (const chat of userChats) {
        const otherUserId = chat.user1Id === userId ? chat.user2Id : chat.user1Id;

        // Check if other user is admin
        const [otherUser] = await db
          .select({ isAdmin: users.isAdmin })
          .from(users)
          .where(eq(users.id, otherUserId));

        // Include chat if other user is admin OR if both users are following each other
        if (otherUser?.isAdmin) {
          validChats.push(chat);
        } else {
          // Check if both users are following each other for non-admin chats
          const [follow1] = await db
            .select()
            .from(follows)
            .where(and(
              eq(follows.followerId, userId),
              eq(follows.followingId, otherUserId)
            ));

          const [follow2] = await db
            .select()
            .from(follows)
            .where(and(
              eq(follows.followerId, otherUserId),
              eq(follows.followingId, userId)
            ));

          if (follow1 && follow2) {
            validChats.push(chat);
          }
        }
      }

      // First, get all chat details with their actual last message times
      const chatDetails = [];
      for (const chat of validChats) {
        const otherUserId = chat.user1Id === userId ? chat.user2Id : chat.user1Id;
        const [otherUser] = await db
          .select()
          .from(users)
          .where(eq(users.id, otherUserId));

        if (!otherUser) continue;

        // Get chat messages to determine real last message time
        const chatMessages = await db
          .select({
            id: messages.id,
            chatId: messages.chatId,
            senderId: messages.senderId,
            content: messages.content,
            createdAt: messages.createdAt
          })
          .from(messages)
          .where(eq(messages.chatId, chat.id))
          .orderBy(messages.createdAt);

        const actualLastMessageTime = chatMessages.length > 0 
          ? chatMessages[chatMessages.length - 1].createdAt 
          : chat.createdAt;

        // Calculate unread messages count based on last read time
        const lastReadTimeKey = `${chat.id}_${userId}_lastRead`;
        const lastReadTime = this.chatReadTimes.get(lastReadTimeKey);

        // Count messages from other user that are newer than last read time
        let unreadCount = 0;
        if (lastReadTime) {
          unreadCount = chatMessages.filter(msg => 
            msg.senderId !== userId && 
            new Date(msg.createdAt) > new Date(lastReadTime)
          ).length;
        } else {
          // If user never read the chat, count all messages from other user
          unreadCount = chatMessages.filter(msg => msg.senderId !== userId).length;
        }

        chatDetails.push({
          id: chat.id,
          otherUserId,
          user: {
            id: otherUser.id,
            username: otherUser.username,
            avatar: otherUser.avatar
          },
          messages: chatMessages,
          lastMessage: chatMessages.length > 0 ? chatMessages[chatMessages.length - 1].content : null,
          lastMessageTime: actualLastMessageTime,
          unreadCount: unreadCount
        });
      }

      // Now deduplicate by keeping only the most recent chat per user
      // Also combine unread counts from all chats with the same user
      const chatsByUser = new Map<number, any>();
      for (const chatDetail of chatDetails) {
        const existingChat = chatsByUser.get(chatDetail.otherUserId);
        if (!existingChat || new Date(chatDetail.lastMessageTime) > new Date(existingChat.lastMessageTime)) {
          // If this is a newer chat, use it but add unread counts from older chats
          const totalUnreadCount = existingChat 
            ? chatDetail.unreadCount + existingChat.unreadCount
            : chatDetail.unreadCount;

          chatsByUser.set(chatDetail.otherUserId, {
            ...chatDetail,
            unreadCount: totalUnreadCount
          });
        } else if (existingChat) {
          // If existing chat is newer, just add this chat's unread count to it
          existingChat.unreadCount += chatDetail.unreadCount;
        }
      }

      // Convert map values to array
      const formattedChats = Array.from(chatsByUser.values());

      // Sort chats by last message time (most recent first)
      formattedChats.sort((a, b) => {
        const timeA = new Date(a.lastMessageTime).getTime();
        const timeB = new Date(b.lastMessageTime).getTime();
        return timeB - timeA; // Descending order (newest first)
      });

      return formattedChats;
    } catch (error) {
      console.error('Error getting user chats:', error);
      return [];
    }
  }

