      const recentPosts30Days = userPosts.filter(post => new Date(post.createdAt) >= thirtyDaysAgo);
      const recentPosts24Hours = userPosts.filter(post => new Date(post.createdAt) >= twentyFourHoursAgo);

      const reach30Days = recentPosts30Days.reduce((sum, post) => sum + (post.viewsCount || 0), 0);
      const reach24Hours = recentPosts24Hours.reduce((sum, post) => sum + (post.viewsCount || 0), 0);

      // Calculate engagement metrics
      const totalLikes = userPosts.reduce((sum, post) => sum + (post.likesCount || 0), 0);
      const totalComments = userPosts.reduce((sum, post) => sum + (post.commentsCount || 0), 0);
      const totalVotes = userPosts.reduce((sum, post) => sum + (post.votesCount || 0), 0);

      return {
        totalPosts,
        videoCount,
        imageCount,
        totalViews,
        totalFollowers,
        totalLikes,
        totalComments,
        totalVotes,
        reach30Days,
        reach24Hours,
        postsLast30Days: recentPosts30Days.length,
        postsLast24Hours: recentPosts24Hours.length,
        engagementRate: totalViews > 0 ? ((totalLikes + totalComments) / totalViews * 100).toFixed(2) : 0
      };
    } catch (error) {
      console.error('Error getting performance stats:', error);
      throw error;
    }
  }

  // Report methods for DatabaseStorage
  async createPostReport(report: any): Promise<any> {
    try {
      // For now, just return a simple response - can be extended later
      return { success: true, id: Date.now() };
    } catch (error) {
      console.error('Error creating post report:', error);
      throw error;
    }
  }

  async createReport(reportData: InsertReport & { reporterId: number }): Promise<Report> {
    try {
      const [report] = await db
        .insert(reports)
        .values(reportData)
        .returning();
      return report;
    } catch (error) {
      console.error('Error creating report:', error);
      throw error;
    }
  }

  async getReportsSummary(): Promise<any[]> {
    try {
      const result = await db
        .select({
          reportedUsername: reports.reportedUsername,
          reportCount: sql<number>`count(*)`,
          latestReport: sql<Date>`max(${reports.createdAt})`
        })
        .from(reports)
        .groupBy(reports.reportedUsername)
        .orderBy(sql<number>`count(*) DESC`);

      return result;
    } catch (error) {
      console.error('Error getting reports summary:', error);
      throw error;
    }
  }

  // Notification methods for DatabaseStorage  
  async getUnreadNotificationCount(userId: number): Promise<number> {
    try {
      const result = await db
        .select({ count: sql<number>`count(*)` })
        .from(notifications)
        .where(and(
          eq(notifications.userId, userId),
          eq(notifications.isRead, false)
        ));
      return result[0]?.count || 0;
    } catch (error) {
      console.error('Error getting unread notification count:', error);
      return 0;
    }
  }

  async markAllNotificationsAsRead(userId: number): Promise<boolean> {
    try {
      await db
        .update(notifications)
        .set({ isRead: true })
        .where(eq(notifications.userId, userId));
      return true;
    } catch (error) {
      console.error('Error marking all notifications as read:', error);
      return false;
    }
  }

  async createPostReport(report: any): Promise<any> {
    try {
      return { success: true, id: Date.now() };
    } catch (error) {
      console.error('Error creating post report:', error);
      throw error;
    }
  }

  async createReport(reportData: any): Promise<any> {
    try {
      return { success: true, id: Date.now() };
    } catch (error) {
      console.error('Error creating report:', error);
      throw error;
    }
  }

  async getReportsSummary(): Promise<any[]> {
    try {
      return [];
    } catch (error) {
      console.error('Error getting reports summary:', error);
      return [];
    }
  }

  // Circle messages methods using in-memory storage for now
  async getCircleMessages(userId: number): Promise<any[]> {
    try {
      const messages = Array.from(this.circleMessages.values())
        .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());

      // Get user likes
      const userLikes = Array.from(this.circleMessageLikes.values())
        .filter(like => like.userId === userId)
        .map(like => like.messageId);

      return messages.map(message => ({
        ...message,
        isLiked: userLikes.includes(message.id)
      }));
    } catch (error) {
      console.error('Error getting circle messages:', error);
      return [];
    }
  }

  async createCircleMessage(userId: number, content: string, imageUrl?: string, videoUrl?: string): Promise<any> {
    try {
      const user = await this.getUser(userId);
      const message = {
        id: this.currentCircleMessageId++,
        userId,
        content,
        imageUrl: imageUrl || null,
        videoUrl: videoUrl || null,
        likesCount: 0,
        createdAt: new Date(),
        user: {
          id: userId,
          username: user?.username || 'Unknown',
          avatar: user?.avatar || null
        }
      };

      this.circleMessages.set(message.id, message);
      return message;
    } catch (error) {
      console.error('Error creating circle message:', error);
      throw error;
    }
  }

  async toggleCircleMessageLike(messageId: number, userId: number): Promise<{ liked: boolean }> {
    try {
      // Check if already liked
      const existingLike = Array.from(this.circleMessageLikes.values())
        .find(like => like.messageId === messageId && like.userId === userId);

      const message = this.circleMessages.get(messageId);
      if (!message) {
        throw new Error('Message not found');
      }

      if (existingLike) {
        // Unlike
        this.circleMessageLikes.delete(existingLike.id);
        message.likesCount = Math.max(0, message.likesCount - 1);
        this.circleMessages.set(messageId, message);
        return { liked: false };
      } else {
        // Like
        const like = {
          id: this.currentCircleMessageLikeId++,
          messageId,
          userId,
          createdAt: new Date()
        };
        this.circleMessageLikes.set(like.id, like);
        message.likesCount += 1;
        this.circleMessages.set(messageId, message);
        return { liked: true };
      }
    } catch (error) {
      console.error('Error toggling circle message like:', error);
      throw error;
    }
  }

  async getUserPerformanceStats(userId: number): Promise<any> {
    try {
      return {
        totalPosts: 0,
        totalLikes: 0,
        totalFollowers: 0,
        engagementRate: 0
      };
    } catch (error) {
      console.error('Error getting user performance stats:', error);
      return null;
    }
  }

  async dismissNotification(notificationId: number): Promise<boolean> {
    try {
      return true;
    } catch (error) {
      console.error('Error dismissing notification:', error);
      return false;
    }
  }

  async getCircleMessages(userId: number): Promise<any[]> {
    try {
      // Get messages from users that the current user follows plus their own messages
      const userFollowing = await db
        .select({ followingId: follows.followingId })
        .from(follows)
        .where(eq(follows.followerId, userId));

      const followingIds = userFollowing.map(f => f.followingId);
      const relevantUserIds = [...followingIds, userId];

      const messages = await db
        .select({
          id: circleMessages.id,
          content: circleMessages.content,
          imageUrl: circleMessages.imageUrl,
          videoUrl: circleMessages.videoUrl,
          likesCount: circleMessages.likesCount,
          commentsCount: circleMessages.commentsCount,
          createdAt: circleMessages.createdAt,
          user: {
            id: users.id,
            username: users.username,
            displayName: users.displayName,
            avatar: users.avatar
          }
        })
        .from(circleMessages)
        .leftJoin(users, eq(circleMessages.userId, users.id))
        .where(inArray(circleMessages.userId, relevantUserIds))
        .orderBy(desc(circleMessages.createdAt));

      return messages;
    } catch (error) {
      console.error('Error getting circle messages:', error);
      return [];
    }
  }

  async createCircleMessage(userId: number, content: string, imageUrl?: string, videoUrl?: string): Promise<any> {
    try {
      const [message] = await db
        .insert(circleMessages)
        .values({
          userId,
          content,
          imageUrl: imageUrl || null,
          videoUrl: videoUrl || null
        })
        .returning();

      // Get user info for the created message
      const user = await db
        .select({
          id: users.id,
          username: users.username,
          displayName: users.displayName,
          avatar: users.avatar
        })
        .from(users)
        .where(eq(users.id, userId))
        .limit(1);

      return {
        ...message,
        user: user[0] || null
      };
    } catch (error) {
      console.error('Error creating circle message:', error);
      throw error;
    }
  }

  async toggleCircleMessageLike(messageId: number, userId: number): Promise<{ liked: boolean }> {
    try {
      // Check if user already liked this message
      const existingLike = await db
        .select()
        .from(circleMessageLikes)
        .where(and(
          eq(circleMessageLikes.messageId, messageId),
          eq(circleMessageLikes.userId, userId)
        ))
        .limit(1);

      if (existingLike.length > 0) {
        // Unlike - remove the like
        await db
          .delete(circleMessageLikes)
          .where(and(
            eq(circleMessageLikes.messageId, messageId),
            eq(circleMessageLikes.userId, userId)
          ));

        // Decrement likes count
        await db
          .update(circleMessages)
          .set({ 
            likesCount: sql`${circleMessages.likesCount} - 1`
          })
          .where(eq(circleMessages.id, messageId));

        return { liked: false };
      } else {
        // Like - add the like
        await db
          .insert(circleMessageLikes)
          .values({
            messageId,
            userId
          });

        // Increment likes count
        await db
          .update(circleMessages)
          .set({ 
            likesCount: sql`${circleMessages.likesCount} + 1`
          })
          .where(eq(circleMessages.id, messageId));

        return { liked: true };
      }
    } catch (error) {
      console.error('Error toggling circle message like:', error);
      throw error;
    }
  }
}

// Using DatabaseStorage for persistent data storage with PostgreSQL
export const storage = new DatabaseStorage();
export const videoCleanup = new VideoCleanupService();

// Schedule cleanup to run every 24 hours
setInterval(async () => {
  try {
    await videoCleanup.cleanupExpiredVideos();
    console.log('Video cleanup completed');
  } catch (error) {
    console.error('Video cleanup failed:', error);
  }
}, 24 * 60 * 60 * 1000); // 24 hours

