import { createContext, useContext, useState, useEffect, ReactNode } from "react";
import { useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface User {
  id: number;
  username: string;
  displayName?: string;
  isAdmin: boolean;
  avatar?: string;
  bio?: string;
  followersCount?: number;
  followingCount?: number;
}

interface AuthContextType {
  user: User | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  login: (user: User, sessionId: string) => void;
  logout: () => void;
  updateUser: (updates: Partial<User>) => void;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

interface AuthProviderProps {
  children: ReactNode;
}

export function AuthProvider({ children }: AuthProviderProps) {
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const queryClient = useQueryClient();

  const isAuthenticated = !!user;

  // Check for existing session on mount
  useEffect(() => {
    const checkAuth = async () => {
      const sessionId = localStorage.getItem('sessionId');
      if (!sessionId) {
        setIsLoading(false);
        return;
      }

      try {
        const response = await fetch('/api/me', {
          headers: {
            'Authorization': `Bearer ${sessionId}`
          }
        });

        if (response.ok) {
          const userData = await response.json();
          setUser(userData);
        } else {
          // Invalid session, clear it
          localStorage.removeItem('sessionId');
        }
      } catch (error) {
        console.error('Auth check failed:', error);
        localStorage.removeItem('sessionId');
      } finally {
        setIsLoading(false);
      }
    };

    checkAuth();
  }, []);

  const login = (userData: User, sessionId: string) => {
    setUser(userData);
    localStorage.setItem('sessionId', sessionId);
  };

  const logout = async () => {
    try {
      const sessionId = localStorage.getItem('sessionId');
      if (sessionId) {
        await apiRequest('POST', '/api/logout');
      }
    } catch (error) {
      console.error('Logout error:', error);
    } finally {
      setUser(null);
      localStorage.removeItem('sessionId');
      queryClient.clear();
    }
  };

  const updateUser = (updates: Partial<User>) => {
    if (user) {
      setUser({ ...user, ...updates });
    }
  };

  const value: AuthContextType = {
    user,
    isAuthenticated,
    isLoading,
    login,
    logout,
    updateUser,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/hooks/use-video-autoplay.tsx.content:
import { useEffect, useRef, useCallback } from 'react';

interface VideoAutoplayHook {
  registerVideo: (postId: number, videoElement: HTMLVideoElement) => void;
  unregisterVideo: (postId: number) => void;
  observePost: (postId: number, element: HTMLElement) => void;
  unobservePost: (postId: number) => void;
}

export function useVideoAutoplay(): VideoAutoplayHook {
  const videoRefs = useRef<Map<number, HTMLVideoElement[]>>(new Map());
  const observerRef = useRef<IntersectionObserver | null>(null);
  const currentPlayingPost = useRef<number | null>(null);
  const userUnmutedGlobal = useRef<boolean>(false); // Persistent unmute state

  const pauseAllVideos = useCallback(() => {
    videoRefs.current.forEach((videos) => {
      videos.forEach(video => {
        if (video && !video.paused) {
          video.pause();
        }
      });
    });
  }, []);

  const playVideosForPost = useCallback((postId: number) => {
    const videos = videoRefs.current.get(postId);
    if (videos) {
      videos.forEach(video => {
        if (video && video.paused) {
          video.muted = !userUnmutedGlobal.current; // Use global unmute state
          video.play().catch(console.error);

          // Add click handler to toggle mute and persist state
          const handleVideoClick = () => {
            video.muted = !video.muted;
            userUnmutedGlobal.current = !video.muted;
          };

          video.removeEventListener('click', handleVideoClick);
          video.addEventListener('click', handleVideoClick);
        }
      });
    }
  }, []);

  const registerVideo = useCallback((postId: number, videoElement: HTMLVideoElement) => {
    const existingVideos = videoRefs.current.get(postId) || [];
    if (!existingVideos.includes(videoElement)) {
      videoRefs.current.set(postId, [...existingVideos, videoElement]);
    }
  }, []);

  const unregisterVideo = useCallback((postId: number) => {
    videoRefs.current.delete(postId);
  }, []);

  const observePost = useCallback((postId: number, element: HTMLElement) => {
    if (!observerRef.current) {
      observerRef.current = new IntersectionObserver(
        (entries) => {
          entries.forEach((entry) => {
            const entryPostId = parseInt(entry.target.getAttribute('data-post-id') || '0');

            if (entry.isIntersecting && entry.intersectionRatio >= 0.6) {
              // Video is mostly visible - play it
              if (currentPlayingPost.current !== entryPostId) {
                pauseAllVideos();
                currentPlayingPost.current = entryPostId;
                playVideosForPost(entryPostId);
              }
            } else if (currentPlayingPost.current === entryPostId && entry.intersectionRatio < 0.3) {
              // Video is mostly out of view - pause it
              pauseAllVideos();
              currentPlayingPost.current = null;
            }
          });
        },
        {
          threshold: [0.3, 0.6], // Multiple thresholds for smooth transitions
          rootMargin: '-20px'
        }
      );
    }

    element.setAttribute('data-post-id', postId.toString());
    observerRef.current.observe(element);
  }, [pauseAllVideos, playVideosForPost]);

  const unobservePost = useCallback((postId: number) => {
    if (observerRef.current) {
      const element = document.querySelector(`[data-post-id="${postId}"]`);
      if (element) {
        observerRef.current.unobserve(element);
      }
    }
  }, []);

  useEffect(() => {
    return () => {
      if (observerRef.current) {
        observerRef.current.disconnect();
      }
    };
  }, []);

  return {
    registerVideo,
    unregisterVideo,
    observePost,
    unobservePost
  };
}

