import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { useToast } from "@/hooks/use-toast";
import { Send, ArrowLeft, Phone, Video, MoreVertical } from "lucide-react";

interface User {
  id: number;
  username: string;
  avatar?: string;
}

interface Message {
  id: number;
  chatId: number;
  senderId: number;
  content: string;
  createdAt: string;
}

interface Chat {
  id: number;
  user: User;
  messages: Message[];
  lastMessage?: string;
  lastMessageTime?: string;
}

export default function ChatNew() {
  const [selectedChat, setSelectedChat] = useState<Chat | null>(null);
  const [messageText, setMessageText] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: user } = useQuery({
    queryKey: ["/api/me"],
    queryFn: async () => {
      const response = await fetch("/api/me", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("sessionId")}`,
        },
      });
      if (!response.ok) throw new Error("Failed to fetch user");
      return response.json();
    },
  });

  const { data: chats = [] } = useQuery({
    queryKey: ["/api/chats"],
    queryFn: async () => {
      const response = await fetch("/api/chats", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("sessionId")}`,
        },
      });
      if (!response.ok) throw new Error("Failed to fetch chats");
      return response.json();
    },
    refetchInterval: 3000,
  });

  const sendMessageMutation = useMutation({
    mutationFn: async ({ chatId, message }: { chatId: number; message: string }) => {
      const response = await fetch(`/api/chats/${chatId}/messages`, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("sessionId")}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ message }),
      });
      if (!response.ok) throw new Error("Failed to send message");
      return response.json();
    },
    onSuccess: (newMessage) => {
      if (selectedChat) {
        const updatedChat = {
          ...selectedChat,
          messages: [...(selectedChat.messages || []), newMessage]
        };
        setSelectedChat(updatedChat);
      }
      queryClient.invalidateQueries({ queryKey: ["/api/chats"] });
      setMessageText("");
    },
    onError: () => {
      toast({
        title: "Failed to send message",
        description: "Please try again",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = () => {
    if (!messageText.trim() || !selectedChat) return;
    sendMessageMutation.mutate({ chatId: selectedChat.id, message: messageText });
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  if (!selectedChat) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-600 via-blue-600 to-cyan-600 p-4">
        <Card className="max-w-md mx-auto">
          <CardHeader>
            <CardTitle className="text-center gradient-text">Chats</CardTitle>
          </CardHeader>
          <CardContent>
            {chats.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                <p>No chats yet</p>
                <p className="text-sm mt-2">Accept friend requests to start chatting</p>
              </div>
            ) : (
              <div className="space-y-3">
                {chats.map((chat: Chat) => (
                  <div
                    key={chat.id}
                    onClick={() => setSelectedChat(chat)}
                    className="flex items-center space-x-3 p-3 rounded-lg hover:bg-gray-50 cursor-pointer transition-colors"
                  >
                    <Avatar className="w-12 h-12">
                      <AvatarImage src={chat.user.avatar} alt={chat.user.username} />
                      <AvatarFallback className="bg-gradient-to-r from-pink-500 to-purple-600 text-white">
                        {chat.user.username[3]?.toUpperCase()}
                      </AvatarFallback>
                    </Avatar>
                    <div className="flex-1 min-w-0">
                      <p className="font-medium text-gray-900">{chat.user.username}</p>
                      <p className="text-sm text-gray-500 truncate">
                        {chat.lastMessage || "Start a conversation"}
                      </p>
                    </div>
                    <div className="text-xs text-gray-400">
                      {chat.lastMessageTime && new Date(chat.lastMessageTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 via-blue-600 to-cyan-600">
      <div className="max-w-md mx-auto bg-white min-h-screen flex flex-col">
        {/* Header */}
        <div className="bg-white border-b p-4 flex items-center space-x-3">
          <Button
            variant="ghost"
            size="sm"
            className="rounded-full p-2"
            onClick={() => setSelectedChat(null)}
          >
            <ArrowLeft className="w-5 h-5" />
          </Button>
          <Avatar className="w-10 h-10">
            <AvatarImage src={selectedChat.user.avatar} alt={selectedChat.user.username} />
            <AvatarFallback className="bg-gradient-to-r from-pink-500 to-purple-600 text-white">
              {selectedChat.user.username[3]?.toUpperCase()}
            </AvatarFallback>
          </Avatar>
          <div className="flex-1">
            <h2 className="font-semibold">{selectedChat.user.username}</h2>
            <p className="text-sm text-gray-500">Online</p>
          </div>
          <div className="flex space-x-2">
            <Button variant="ghost" size="sm" className="rounded-full">
              <Phone className="w-5 h-5" />
            </Button>
            <Button variant="ghost" size="sm" className="rounded-full">
              <Video className="w-5 h-5" />
            </Button>
            <Button variant="ghost" size="sm" className="rounded-full">
              <MoreVertical className="w-5 h-5" />
            </Button>
          </div>
        </div>

        {/* Messages */}
        <div 
          className="flex-1 overflow-y-auto p-4 space-y-4" 
          style={{ 
            backgroundImage: 'linear-gradient(to bottom, #1e3a8a, #059669)', 
            minHeight: 'calc(100vh - 140px)' 
          }}
        >
          {!selectedChat.messages || selectedChat.messages.length === 0 ? (
            <div className="text-center text-white/70 mt-8">
              <p>No messages yet. Start the conversation!</p>
            </div>
          ) : (
            selectedChat.messages.map((message: Message) => (
              <div key={message.id} className={`flex ${message.senderId === user?.id ? 'justify-end' : 'justify-start'}`}>
                {message.senderId !== user?.id && (
                  <Avatar className="w-8 h-8 mr-2 mt-1">
                    <AvatarImage src={selectedChat.user.avatar} alt={selectedChat.user.username} />
                    <AvatarFallback className="bg-gradient-to-r from-pink-500 to-purple-600 text-white text-xs">
                      {selectedChat.user.username[3]?.toUpperCase()}
                    </AvatarFallback>
                  </Avatar>
                )}
                <div className={`max-w-xs px-4 py-2 rounded-2xl shadow-sm ${
                  message.senderId === user?.id 
                    ? 'bg-blue-500 text-white rounded-br-sm' 
                    : 'bg-white text-gray-900 rounded-bl-sm'
                }`}>
                  <p className="text-sm leading-relaxed">{message.content}</p>
                  <p className={`text-xs mt-1 ${
                    message.senderId === user?.id ? 'text-blue-100' : 'text-gray-400'
                  }`}>
                    {new Date(message.createdAt).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                  </p>
                </div>
              </div>
            ))
          )}
        </div>

        {/* Message Input */}
        <div className="bg-white border-t p-3">
          <div className="flex items-center space-x-2">
            <div className="flex-1 bg-gray-100 rounded-full px-4 py-3 flex items-center">
              <Input 
                placeholder="Type a message..." 
                value={messageText} 
                onChange={(e) => setMessageText(e.target.value)} 
                onKeyPress={handleKeyPress}
                className="border-none bg-transparent focus:ring-0 focus:outline-none text-sm flex-1" 
                disabled={sendMessageMutation.isPending}
              />
            </div>
            {messageText.trim() && (
              <Button 
                onClick={handleSendMessage} 
                disabled={sendMessageMutation.isPending}
                className="rounded-full bg-blue-500 hover:bg-blue-600 text-white p-3"
              >
                <Send className="w-5 h-5" />
              </Button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/chat.tsx.content:
import { useState, useEffect, useRef } from "react";
import { useParams, useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { ArrowLeft, Send, Camera, Mic, Paperclip, Image, Phone, Video, MoreVertical, FileText, MapPin, User, Calendar, Headphones, ImageIcon } from "lucide-react";
import { CallInterface } from "@/components/call/call-interface";
import { useWebRTCCall } from "@/hooks/use-webrtc-call";
import { useCallState } from "@/hooks/use-call-state";
import { useToast } from "@/hooks/use-toast";

export default function ChatPage() {
  const { chatId } = useParams();
  const [, setLocation] = useLocation();
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [message, setMessage] = useState("");
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [uploadingFiles, setUploadingFiles] = useState<Map<string, { file: File, progress: number, preview: string }>>(new Map());
  const [fullScreenMedia, setFullScreenMedia] = useState<{ url: string, type: 'image' | 'video' } | null>(null);
  const [isTyping, setIsTyping] = useState(false);
  const [otherUserTyping, setOtherUserTyping] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [showAttachmentMenu, setShowAttachmentMenu] = useState(false);
  const [showChatMenu, setShowChatMenu] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const imageInputRef = useRef<HTMLInputElement>(null);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioChunksRef = useRef<Blob[]>([]);
  const typingTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // WebRTC Call functionality
  const { startCall, acceptCall, declineCall, endCall, toggleMute } = useWebRTCCall();
  const { isCallActive, isIncoming, callStatus, caller, duration } = useCallState();

  // Fetch chat data
  const { data: chat, isLoading } = useQuery({
    queryKey: [`/api/chats/${chatId}`],
    enabled: !!chatId,
  }) as { data: any, isLoading: boolean };

  // Fetch messages with faster polling for smooth experience
  const { data: messages = [] } = useQuery({
    queryKey: [`/api/chats/${chatId}/messages`],
    enabled: !!chatId,
    refetchInterval: 500, // 0.5 second polling for instant messaging feel
    refetchOnWindowFocus: true,
    refetchOnMount: true,
  }) as { data: any[] };

  // WhatsApp-style file upload with instant preview
  const uploadFileInstantly = async (file: File) => {
    const fileId = Date.now().toString();
    const preview = URL.createObjectURL(file);

    // Add to uploading files with preview
    setUploadingFiles(prev => new Map(prev.set(fileId, { file, progress: 0, preview })));

    try {
      const sessionId = localStorage.getItem('sessionId');
      const formData = new FormData();
      formData.append('file', file);
      formData.append('message', message || '');

      // Simulate upload progress
      const progressInterval = setInterval(() => {
        setUploadingFiles(prev => {
          const current = prev.get(fileId);
          if (current && current.progress < 90) {
            return new Map(prev.set(fileId, { ...current, progress: current.progress + 10 }));
          }
          return prev;
        });
      }, 100);

      const response = await fetch(`/api/chats/${chatId}/upload`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${sessionId}`
        },
        body: formData,
      });

      clearInterval(progressInterval);

      if (response.ok) {
        // Complete upload
        setUploadingFiles(prev => {
          const newMap = new Map(prev);
          newMap.delete(fileId);
          return newMap;
        });
        queryClient.invalidateQueries({ queryKey: [`/api/chats/${chatId}/messages`] });
        setMessage("");
      } else {
        throw new Error('Upload failed');
      }
    } catch (error) {
      setUploadingFiles(prev => {
        const newMap = new Map(prev);
        newMap.delete(fileId);
        return newMap;
      });
      toast({
        title: "Upload failed",
        description: "Failed to send media",
        variant: "destructive",
      });
    }
  };

  // Send message mutation with optimistic updates for instant messaging
  const sendMessageMutation = useMutation({
    mutationFn: async (content: string) => {
      const sessionId = localStorage.getItem('sessionId');
      const headers: Record<string, string> = {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${sessionId}`
      };

      const response = await fetch(`/api/chats/${chatId}/messages`, {
        method: 'POST',
        headers,
        body: JSON.stringify({ message: content }),
      });
      if (!response.ok) {
        const errorData = await response.json().catch(() => null);
        throw new Error(errorData?.message || 'Failed to send message');
      }
      return response.json();
    },
    onMutate: async (content: string) => {
      // Cancel any outgoing refetches (so they don't overwrite our optimistic update)
      await queryClient.cancelQueries({ queryKey: [`/api/chats/${chatId}/messages`] });

      // Snapshot the previous value
      const previousMessages = queryClient.getQueryData([`/api/chats/${chatId}/messages`]);

      // Optimistically update to the new value - show message immediately
      const optimisticMessage = {
        id: Date.now(), // temporary ID
        chatId: parseInt(chatId || '0'),
        senderId: user?.id,
        message: content,
        createdAt: new Date().toISOString(),
        sender: {
          id: user?.id,
          username: user?.username,
          avatar: user?.avatar
        },
        isOptimistic: true // flag to identify optimistic messages
      };

      queryClient.setQueryData([`/api/chats/${chatId}/messages`], (old: any[]) => {
        return [...(old || []), optimisticMessage];
      });

      // Return a context object with the snapshotted value
      return { previousMessages };
    },
    onSuccess: (data) => {
      // Immediately invalidate to get the real message from server
      queryClient.invalidateQueries({ queryKey: [`/api/chats/${chatId}/messages`] });
      queryClient.invalidateQueries({ queryKey: ["/api/chats"] });
      setMessage("");
      setSelectedFile(null);
    },
    onError: (error: any, content, context) => {
      // If the mutation fails, use the context returned from onMutate to roll back
      queryClient.setQueryData([`/api/chats/${chatId}/messages`], context?.previousMessages);
      toast({
        title: "Message failed",
        description: error.message || "Failed to send message. Please try again.",
        variant: "destructive",
      });
    },
    onSettled: () => {
      // Always refetch after error or success to ensure server state
      queryClient.invalidateQueries({ queryKey: [`/api/chats/${chatId}/messages`] });
    }
  });

  // Kick out mutation to remove user from friend list
  const kickOutMutation = useMutation({
    mutationFn: async () => {
      const sessionId = localStorage.getItem('sessionId');
      const response = await fetch(`/api/friends/remove`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${sessionId}`
        },
        body: JSON.stringify({ friendId: chat?.user?.id }),
      });
      if (!response.ok) throw new Error('Failed to remove friend');
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "User removed",
        description: "User has been removed from your friend list",
      });
      setLocation("/");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to remove user",
        variant: "destructive",
      });
    }
  });

  // Mark chat as read mutation
  const markAsReadMutation = useMutation({
    mutationFn: async (chatId: number) => {
      const sessionId = localStorage.getItem('sessionId');
      if (!sessionId) {
        throw new Error('No session ID found');
      }

      console.log(`Marking chat ${chatId} as read...`);
      const response = await fetch(`/api/chats/${chatId}/read`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${sessionId}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Mark as read failed:', response.status, errorText);
        throw new Error(`Failed to mark as read: ${response.status}`);
      }

      console.log(`Chat ${chatId} marked as read successfully`);
      return response.json();
    },
    onSuccess: () => {
      console.log('Invalidating chats query after mark as read');
      // Invalidate chats list to update unread counts
      queryClient.invalidateQueries({ queryKey: ['/api/chats'] });
    },
    onError: (error) => {
      console.error('Mark as read mutation failed:', error);
    }
  });

  const handleSendMessage = () => {
    if (message.trim() || selectedFile) {
      const messageToSend = message.trim();
      if (messageToSend) {
        sendMessageMutation.mutate(messageToSend);
      }
      if (selectedFile) {
        uploadFileInstantly(selectedFile);
      }
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Enhanced typing indicator with server notification
  const handleTyping = async () => {
    setIsTyping(true);

    // Send typing indicator to server (silently fail if not available)
    try {
      const sessionId = localStorage.getItem('sessionId');
      fetch(`/api/chats/${chatId}/typing`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${sessionId}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ isTyping: true })
      }).catch(() => {});
    } catch (error) {
      // Silently fail for typing indicators
    }

    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }

    typingTimeoutRef.current = setTimeout(async () => {
      setIsTyping(false);
      // Send stop typing indicator
      try {
        const sessionId = localStorage.getItem('sessionId');
        fetch(`/api/chats/${chatId}/typing`, {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${sessionId}`,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ isTyping: false })
        }).catch(() => {});
      } catch (error) {
        // Silently fail
      }
    }, 2000);
  };

  const startVoiceRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        audioChunksRef.current.push(event.data);
      };

      mediaRecorder.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/mp3' });
        await uploadVoiceMessage(audioBlob);
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorder.start();
      setIsRecording(true);
    } catch (error) {
      toast({
        title: "Microphone Access",
        description: "Please allow microphone access to record voice messages",
        variant: "destructive",
      });
    }
  };

  const stopVoiceRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }
  };

  const uploadVoiceMessage = async (audioBlob: Blob) => {
    const formData = new FormData();
    formData.append('audio', audioBlob, 'voice-message.mp3');

    try {
      const response = await fetch(`/api/chats/${chatId}/voice`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('sessionId')}`
