import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { Search, MessageCircle, UserPlus, Plus, Send, Heart, MoreHorizontal, X } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";
import { Link } from "wouter";
import { Navigation } from "@/components/layout/navigation";
import { VibeUpload } from "@/components/vibe/vibe-upload";
import { format } from "date-fns";
import { apiRequest } from "@/lib/queryClient";

export default function Circle() {
  const [searchQuery, setSearchQuery] = useState("");
  const [showVibeUpload, setShowVibeUpload] = useState(false);
  const [newMessage, setNewMessage] = useState("");
  const [selectedTab, setSelectedTab] = useState("chats");
  const [selectedVibe, setSelectedVibe] = useState<any>(null);
  const [showVibeModal, setShowVibeModal] = useState(false);
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Circle messages for timeline (different from regular chats)
  const { data: circleMessages = [] } = useQuery({
    queryKey: ["/api/circle/messages"],
    refetchInterval: 3000,
  });

  const { data: friendRequests = [] } = useQuery({
    queryKey: ["/api/friend-requests"],
    refetchInterval: 5000,
  });

  const { data: vibes = [] } = useQuery({
    queryKey: ["/api/vibes"],
    refetchInterval: 5000,
  });

  const { data: searchResults = [], isLoading: isSearching } = useQuery({
    queryKey: ["/api/users/search", searchQuery],
    queryFn: async () => {
      if (!searchQuery || searchQuery.length < 2) return [];

      const response = await fetch(`/api/users/search?q=${encodeURIComponent(searchQuery)}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("sessionId")}`,
        },
      });

      if (!response.ok) throw new Error("Search failed");
      const data = await response.json();
      return Array.isArray(data) ? data : [];
    },
    enabled: searchQuery.length >= 2,
    staleTime: 30000,
  });

  const { data: chats = [] } = useQuery({
    queryKey: ["/api/chats"],
    refetchInterval: 5000,
  });

  // Post Circle message to timeline
  const postCircleMessageMutation = useMutation({
    mutationFn: async (content: string) => {
      return apiRequest("POST", "/api/circle/messages", { content });
    },
    onSuccess: () => {
      setNewMessage("");
      queryClient.invalidateQueries({ queryKey: ["/api/circle/messages"] });
      toast({
        title: "Message posted",
        description: "Your message has been shared in your circle.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to post message",
        description: error.message || "Something went wrong.",
        variant: "destructive",
      });
    }
  });

  const sendFriendRequestMutation = useMutation({
    mutationFn: async (userId: number) => {
      return apiRequest("POST", `/api/friend-requests/${userId}`, {});
    },
    onSuccess: () => {
      toast({
        title: "Friend request sent",
        description: "Your friend request has been sent successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/friend-requests"] });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to send request",
        description: error.message || "Something went wrong.",
        variant: "destructive",
      });
    },
  });

  const respondToFriendRequestMutation = useMutation({
    mutationFn: async ({ requestId, action }: { requestId: number; action: "accept" | "reject" }) => {
      return apiRequest("POST", `/api/friend-requests/${requestId}/${action}`, {});
    },
    onSuccess: (_, variables) => {
      toast({
        title: variables.action === "accept" ? "Friend request accepted" : "Friend request declined",
        description: variables.action === "accept" 
          ? "You are now friends and can share private moments!" 
          : "Friend request has been declined.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/friend-requests"] });
      queryClient.invalidateQueries({ queryKey: ["/api/chats"] });
    },
    onError: (error: any) => {
      toast({
        title: "Action failed",
        description: error.message || "Something went wrong.",
        variant: "destructive",
      });
    },
  });

  const toggleLikeMutation = useMutation({
    mutationFn: async (messageId: number) => {
      return apiRequest("POST", `/api/circle/messages/${messageId}/like`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/circle/messages"] });
    },
    onError: (error: any) => {
      toast({
        title: "Action failed",
        description: error.message || "Something went wrong.",
        variant: "destructive",
      });
    }
  });

  const handlePostMessage = () => {
    if (newMessage.trim()) {
      postCircleMessageMutation.mutate(newMessage.trim());
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Navigation />

      <div className="max-w-md mx-auto bg-white min-h-screen pb-nav">
        {/* Header like home page */}
        <header className="bg-white border-b border-gray-200 sticky top-0 z-50">
          <div className="px-4 py-3 flex items-center justify-between">
            <h1 className="text-xl md:text-2xl font-bold gradient-text">
              Your Circle
            </h1>
          </div>
        </header>

        {/* Search */}
        <div className="px-4 py-3 bg-white border-b">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
            <Input
              placeholder="Search users by username..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 bg-gray-100 border-0 rounded-full"
            />
          </div>
        </div>

        {/* Search Results */}
        {searchQuery.length >= 2 && (
          <div className="bg-white border-b">
            <div className="p-4">
              <h3 className="font-semibold mb-3">Search Results</h3>
              {isSearching ? (
                <div className="text-center py-4">
                  <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-pink-500 mx-auto"></div>
                </div>
              ) : searchResults.length === 0 ? (
                <p className="text-gray-500 text-center py-4">No users found</p>
              ) : (
                <div className="space-y-3">
                  {searchResults.map((searchUser: any) => (
                    <div key={searchUser.id} className="flex items-center justify-between p-2">
                      <div className="flex items-center space-x-3">
                        <Avatar className="w-10 h-10">
                          <AvatarImage src={searchUser.avatar} alt={searchUser.username} />
                          <AvatarFallback className="bg-gradient-to-r from-pink-500 to-purple-500 text-white">
                            {searchUser.username?.[0]?.toUpperCase()}
                          </AvatarFallback>
                        </Avatar>
                        <div>
                          <h4 className="font-medium text-sm">{searchUser.username}</h4>
                          <p className="text-xs text-gray-500">{searchUser.displayName}</p>
                        </div>
                      </div>
                      <Button
                        size="sm"
                        onClick={() => sendFriendRequestMutation.mutate(searchUser.id)}
                        disabled={sendFriendRequestMutation.isPending}
                        className="bg-gradient-to-r from-pink-500 to-purple-500 hover:opacity-90 text-xs px-3 py-1 h-8"
                      >
                        <UserPlus className="w-3 h-3 mr-1" />
                        Add
                      </Button>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}

        {/* Circle's Vibe Section */}
        <div className="p-4 bg-white border-b">
          <div className="flex items-center justify-between mb-4">
            <h3 className="font-semibold text-lg">Circle's Vibe</h3>
            <Button
              onClick={() => setShowVibeUpload(true)}
              size="sm"
              className="bg-gradient-to-r from-pink-500 to-purple-500 hover:opacity-90 rounded-full w-8 h-8 p-0"
            >
              <Plus className="w-4 h-4" />
            </Button>
          </div>

          <div className="flex space-x-4 overflow-x-auto pb-2">
            {/* Sort vibes: User's own vibes first, then others */}
            {vibes
              .filter((vibe: any) => vibe.imageUrl || vibe.videoUrl)
              .sort((a: any, b: any) => {
                // Put current user's vibes first
                if (a.userId === user?.id && b.userId !== user?.id) return -1;
                if (a.userId !== user?.id && b.userId === user?.id) return 1;
                return 0;
              })
              .map((vibe: any) => {
                const isOwnVibe = vibe.userId === user?.id;
                return (
                  <div 
                    key={vibe.id} 
                    className="flex-shrink-0 text-center cursor-pointer"
                    onClick={() => {
                      setSelectedVibe(vibe);
                      setShowVibeModal(true);
                    }}
                  >
                    <Avatar className="w-16 h-16 ring-2 ring-pink-500 ring-offset-2 hover:ring-4 transition-all">
                      <AvatarImage 
                        src={vibe.imageUrl || vibe.videoUrl || '/placeholder-vibe.jpg'} 
                        alt="Vibe" 
                      />
                      <AvatarFallback className="bg-gradient-to-r from-pink-500 to-purple-500 text-white">
                        {vibe.user?.username?.[0]?.toUpperCase()}
                      </AvatarFallback>
                    </Avatar>
                    <p className="text-xs mt-2 truncate w-16 font-medium">
                      {isOwnVibe ? 'Your vibe' : vibe.user?.username}
                    </p>
                  </div>
                );
              })}

            {/* Show "Your vibe" placeholder only if user has no vibes */}
            {vibes.filter((vibe: any) => vibe.userId === user?.id && (vibe.imageUrl || vibe.videoUrl)).length === 0 && (
              <div className="flex-shrink-0 text-center">
                <div className="relative">
                  <Avatar className="w-16 h-16 ring-2 ring-gray-300 ring-offset-2">
                    <AvatarImage src={user?.avatar} alt={user?.username} />
                    <AvatarFallback className="bg-gray-300 text-gray-600 text-lg">
                      {user?.username?.[0]?.toUpperCase()}
                    </AvatarFallback>
                  </Avatar>
                </div>
                <p className="text-xs mt-2 font-medium text-gray-500">Your vibe</p>
              </div>
            )}
          </div>
        </div>

        {/* Timeline Messages Section */}
        <div className="flex-1 bg-white">
          {/* Circle Messages Timeline */}
          <div className="space-y-4 p-4">
            {circleMessages.length > 0 && circleMessages.map((message: any) => (
                <div key={message.id} className="bg-gray-50 rounded-lg p-3">
                  <div className="flex items-start space-x-3">
                    <Avatar className="w-10 h-10">
                      <AvatarImage src={message.user?.avatar} alt={message.user?.username} />
                      <AvatarFallback className="bg-gradient-to-r from-pink-500 to-purple-500 text-white">
                        {message.user?.username?.[0]?.toUpperCase()}
                      </AvatarFallback>
                    </Avatar>
                    <div className="flex-1">
                      <div className="flex items-center space-x-2 mb-1">
                        <h4 className="font-medium text-sm">{message.user?.username}</h4>
                        <span className="text-xs text-gray-500">
                          {format(new Date(message.createdAt), 'HH:mm')}
                        </span>
                      </div>
                      <p className="text-sm text-gray-700 mb-2">{message.content}</p>
                      <div className="flex items-center space-x-4">
                        <button
                          onClick={() => toggleLikeMutation.mutate(message.id)}
                          className="flex items-center space-x-1 text-gray-500 hover:text-pink-500 transition-colors"
                        >
                          <Heart className={`w-4 h-4 ${message.isLiked ? 'fill-pink-500 text-pink-500' : ''}`} />
                          <span className="text-xs">{message.likesCount || 0}</span>
                        </button>
                        <button className="text-gray-400 hover:text-gray-600">
                          <MoreHorizontal className="w-4 h-4" />
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
            ))}
          </div>
        </div>

        {/* Bottom Tabs */}
        <div className="bg-white border-t p-4">
          <Tabs value={selectedTab} onValueChange={setSelectedTab} className="w-full">
            <TabsList className="grid w-full grid-cols-2 bg-gray-100">
              <TabsTrigger value="chats" className="flex items-center space-x-2">
                <MessageCircle className="w-4 h-4" />
                <span>Chats</span>
                <span className="bg-pink-500 text-white rounded-full text-xs px-2 py-0.5 ml-1">
                  {chats.length}
                </span>
              </TabsTrigger>
              <TabsTrigger value="requests" className="flex items-center space-x-2">
                <UserPlus className="w-4 h-4" />
                <span>Requests</span>
                {friendRequests.length > 0 && (
                  <span className="bg-red-500 text-white rounded-full text-xs px-2 py-0.5 ml-1">
                    {friendRequests.length}
                  </span>
                )}
              </TabsTrigger>
            </TabsList>

            <TabsContent value="chats" className="mt-4">
              {chats.length === 0 ? (
                <div className="text-center py-8">
                  <p className="text-gray-500 mb-2">No chats yet</p>
                  <p className="text-sm text-gray-400">Add friends to start chatting!</p>
                </div>
              ) : (
                <div className="space-y-3">
                  {chats.map((chat: any) => (
                    <Link key={chat.id} href={`/chat/${chat.id}`}>
                      <div className="flex items-center space-x-3 p-3 hover:bg-gray-50 rounded-lg cursor-pointer">
                        <Avatar className="w-12 h-12">
                          <AvatarImage src={chat.user?.avatar} alt={chat.user?.username} />
                          <AvatarFallback className="bg-gradient-to-r from-pink-500 to-purple-500 text-white">
                            {chat.user?.username?.[0]?.toUpperCase()}
                          </AvatarFallback>
                        </Avatar>
                        <div className="flex-1">
                          <h4 className="font-medium">{chat.user?.username}</h4>
                          <p className="text-sm text-gray-500">{chat.lastMessage || "Start chatting"}</p>
                        </div>
                        <div className="text-right flex items-center">
                          {/* Show notification badge for unread messages */}
                          {chat.unreadCount && chat.unreadCount > 0 ? (
                            <div className="bg-red-500 text-white rounded-full min-w-[20px] h-5 flex items-center justify-center px-1">
                              <span className="text-xs font-medium">
                                {chat.unreadCount > 99 ? '99+' : chat.unreadCount}
                              </span>
                            </div>
                          ) : (
                            <div className="w-3 h-3 rounded-full bg-gray-300"></div>
                          )}
                        </div>
                      </div>
                    </Link>
                  ))}
                </div>
              )}
            </TabsContent>

            <TabsContent value="requests" className="mt-4">
              {friendRequests.length === 0 ? (
                <div className="text-center py-8">
                  <p className="text-gray-500 mb-2">No friend requests</p>
                  <p className="text-sm text-gray-400">People you add will appear here</p>
                </div>
              ) : (
                <div className="space-y-3">
                  {friendRequests.map((request: any) => (
                    <div key={request.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <div className="flex items-center space-x-3">
                        <Avatar className="w-12 h-12">
                          <AvatarImage src={request.user?.avatar} alt={request.user?.username} />
                          <AvatarFallback className="bg-gradient-to-r from-pink-500 to-purple-500 text-white">
                            {request.user?.username?.[0]?.toUpperCase()}
                          </AvatarFallback>
                        </Avatar>
                        <div>
                          <h4 className="font-medium">{request.user?.username}</h4>
                          <p className="text-sm text-gray-500">Wants to be friends</p>
                        </div>
                      </div>
                      <div className="flex space-x-2">
                        <Button
                          size="sm"
                          onClick={() => respondToFriendRequestMutation.mutate({ requestId: request.id, action: "accept" })}
                          disabled={respondToFriendRequestMutation.isPending}
                          className="bg-gradient-to-r from-green-500 to-emerald-500 hover:opacity-90 text-xs px-3 py-1 h-8"
                        >
                          Accept
                        </Button>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => respondToFriendRequestMutation.mutate({ requestId: request.id, action: "reject" })}
                          disabled={respondToFriendRequestMutation.isPending}
                          className="text-xs px-3 py-1 h-8"
                        >
                          Decline
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </TabsContent>
          </Tabs>
        </div>
      </div>

      {/* Vibe Upload Modal */}
      {showVibeUpload && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg max-w-md w-full max-h-[90vh] overflow-y-auto">
            <VibeUpload
              onClose={() => setShowVibeUpload(false)}
              onSuccess={() => {
                setShowVibeUpload(false);
                queryClient.invalidateQueries({ queryKey: ["/api/vibes"] });
              }}
            />
          </div>
        </div>
      )}

      {/* Full Screen Vibe Viewer - Instagram Stories Style */}
      {showVibeModal && selectedVibe && (
        <div 
          className="fixed inset-0 bg-black z-50 flex items-center justify-center"
          onClick={(e) => {
            // Close modal when clicking on the background
            if (e.target === e.currentTarget) {
              setShowVibeModal(false);
              setSelectedVibe(null);
            }
          }}
        >
          {/* Close Button */}
          <button
            onClick={(e) => {
              e.preventDefault();
              e.stopPropagation();
              setShowVibeModal(false);
              setSelectedVibe(null);
            }}
            className="absolute top-4 right-4 z-[100] bg-black bg-opacity-50 rounded-full p-2 text-white hover:bg-opacity-70 transition-all focus:outline-none focus:ring-2 focus:ring-white"
          >
            <X className="w-6 h-6" />
          </button>

          {/* User Info */}
          <div className="absolute top-4 left-4 z-[90] flex items-center space-x-3 text-white">
            <Avatar className="w-10 h-10 ring-2 ring-white">
              <AvatarImage src={selectedVibe.user?.avatar} alt={selectedVibe.user?.username} />
              <AvatarFallback className="bg-gray-600 text-white">
                {selectedVibe.user?.username?.[0]?.toUpperCase()}
              </AvatarFallback>
            </Avatar>
            <div>
              <p className="font-semibold text-sm">{selectedVibe.user?.username}</p>
              <p className="text-xs opacity-75">{new Date(selectedVibe.createdAt).toLocaleTimeString()}</p>
            </div>
          </div>

          {/* Content - Portrait 9:16 format like Instagram Reels/WhatsApp Status */}
          <div className="relative w-full h-full flex items-center justify-center z-10">
            <div className="relative w-full max-w-[400px] aspect-[9/16] bg-black overflow-hidden rounded-lg">
              {selectedVibe.videoUrl ? (
                <video
                  src={selectedVibe.videoUrl}
                  className="w-full h-full object-cover"
                  controls
                  autoPlay
                  loop
                  muted
                  playsInline
                />
              ) : selectedVibe.imageUrl ? (
                <img
                  src={selectedVibe.imageUrl}
                  alt="Vibe"
                  className="w-full h-full object-cover"
                />
              ) : (
                <div className="w-full h-full flex items-center justify-center text-white text-center">
                  <div>
                    <p className="text-lg mb-4">No media content</p>
                    {selectedVibe.title && (
                      <p className="text-gray-300">{selectedVibe.title}</p>
                    )}
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Title/Caption */}
          {selectedVibe.title && (
            <div className="absolute bottom-4 left-4 right-4 z-[90] text-white">
              <p className="text-center bg-black bg-opacity-50 rounded-lg p-3">
                {selectedVibe.title}
              </p>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

client/src/pages/create.tsx.content:
import { Header } from "@/components/layout/header";
import { Navigation } from "@/components/layout/navigation";
import { CreateRegularPost } from "@/components/post/create-regular-post";
import { CreateVideoPost } from "@/components/post/create-video-post";
import { useAuth } from "@/hooks/use-auth";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Video, Image } from "lucide-react";
import { useState } from "react";
import Auth from "./auth";

export default function Create() {
  const { user, isAuthenticated } = useAuth();
  const [activeTab, setActiveTab] = useState<"video" | "regular">(user?.isAdmin ? "video" : "regular");

  if (!isAuthenticated) {
    return <Auth />;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="max-w-2xl mx-auto px-4 py-6 pb-nav">
        {user?.isAdmin && (
          <Card className="mb-4">
            <CardContent className="p-4">
              <div className="flex items-center space-x-2">
                <Button
                  variant={activeTab === "video" ? "default" : "outline"}
                  size="sm"
                  className={`flex items-center space-x-2 ${
                    activeTab === "video"
                      ? "gradient-bg text-white"
                      : "hover:bg-gray-100"
                  }`}
                  onClick={() => setActiveTab("video")}
                >
                  <Video className="w-4 h-4" />
                  <span>Admin Video Post</span>
                </Button>

                <Button
                  variant={activeTab === "regular" ? "default" : "outline"}
                  size="sm"
                  className={`flex items-center space-x-2 ${
                    activeTab === "regular"
                      ? "gradient-bg text-white"
                      : "hover:bg-gray-100"
                  }`}
                  onClick={() => setActiveTab("regular")}
                >
                  <Image className="w-4 h-4" />
                  <span>Regular Post</span>
                </Button>
              </div>
            </CardContent>
          </Card>
        )}

        <Card>
          <CardHeader>
            <CardTitle className="gradient-text">
              {user?.isAdmin && activeTab === "video" 
                ? "Create Trending Video Post" 
                : user?.isAdmin 
                ? "Create Regular Post" 
                : "Create Post"
              }
            </CardTitle>
            <p className="text-gray-600">
              {user?.isAdmin && activeTab === "video"
                ? "Create trending video content with rankings and categories"
                : user?.isAdmin 
                ? "Share regular content with the community"
                : "Share your content with the community"
              }
            </p>
          </CardHeader>
          <CardContent>
            {user?.isAdmin && activeTab === "video" ? (
              <CreateVideoPost />
            ) : (
              <CreateRegularPost />
            )}
          </CardContent>
        </Card>
      </div>

      <Navigation />
    </div>
  );
}

client/src/pages/cv.tsx.content:
import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { ArrowLeft, Save, Plus, X, FileText, User, Briefcase, GraduationCap, Award, Languages, Phone, Mail, MapPin, Calendar, Trash2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Link, useRoute } from "wouter";

interface CVData {
  fullName: string;
  accountType: 'business' | 'creator' | 'government';
  email: string;
  phone: string;
  location: string;
  summary: string;
  experience: Array<{
    id: string;
    company: string;
    position: string;
    duration: string;
    description: string;
  }>;
  education: Array<{
    id: string;
    institution: string;
    degree: string;
    year: string;
    description: string;
  }>;
  skills: string[];
  languages: string[];
  achievements: Array<{
    id: string;
    title: string;
    description: string;
    date: string;
  }>;
  projects: Array<{
    id: string;
    name: string;
    description: string;
    technologies: string;
    link: string;
  }>;
}

export default function CVPage() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [match, params] = useRoute("/cv/:userId");

  const isViewingOtherUser = !!params?.userId;
  const targetUserId = params?.userId || user?.id;

  const [cvData, setCvData] = useState<CVData>({
    fullName: '',
    accountType: 'creator',
    email: '',
    phone: '',
    location: '',
    summary: '',
    experience: [],
    education: [],
    skills: [],
    languages: [],
    achievements: [],
    projects: []
  });

  const [newSkill, setNewSkill] = useState('');
  const [newLanguage, setNewLanguage] = useState('');

  // Fetch CV data
  const { data: savedCV, isLoading } = useQuery({
    queryKey: isViewingOtherUser ? ['/api/cv', targetUserId] : ['/api/cv'],
    queryFn: async () => {
      if (isViewingOtherUser) {
        const response = await apiRequest('GET', `/api/users/${targetUserId}/cv`);
        return response.json();
      } else {
        const response = await apiRequest('GET', '/api/cv');
        return response.json();
      }
    },
    enabled: !!user && !!targetUserId,
  });

  // Save CV mutation
  const saveCVMutation = useMutation({
    mutationFn: async (data: CVData) => {
      const response = await apiRequest('POST', '/api/cv', data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/cv'] });
      toast({
        title: "CV Saved",
        description: "Your CV has been saved successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to save CV",
        variant: "destructive",
      });
    }
  });

  useEffect(() => {
    if (savedCV && typeof savedCV === 'object') {
      setCvData(savedCV as CVData);
    } else if (user) {
      setCvData(prev => ({
        ...prev,
        fullName: user.displayName || user.username || '',
        email: ''
      }));
    }
  }, [savedCV, user]);

  const addExperience = () => {
    setCvData(prev => ({
      ...prev,
      experience: [...prev.experience, {
        id: Date.now().toString(),
        company: '',
        position: '',
        duration: '',
        description: ''
      }]
    }));
  };

  const updateExperience = (id: string, field: string, value: string) => {
    setCvData(prev => ({
      ...prev,
      experience: prev.experience.map(exp => 
        exp.id === id ? { ...exp, [field]: value } : exp
      )
    }));
  };

  const removeExperience = (id: string) => {
    setCvData(prev => ({
      ...prev,
      experience: prev.experience.filter(exp => exp.id !== id)
    }));
  };

  const addEducation = () => {
    setCvData(prev => ({
      ...prev,
      education: [...prev.education, {
        id: Date.now().toString(),
        institution: '',
        degree: '',
        year: '',
        description: ''
      }]
    }));
  };

  const updateEducation = (id: string, field: string, value: string) => {
    setCvData(prev => ({
      ...prev,
      education: prev.education.map(edu => 
        edu.id === id ? { ...edu, [field]: value } : edu
      )
    }));
  };

  const removeEducation = (id: string) => {
    setCvData(prev => ({
      ...prev,
      education: prev.education.filter(edu => edu.id !== id)
    }));
  };

  const addSkill = () => {
    if (newSkill.trim()) {
      setCvData(prev => ({
        ...prev,
        skills: [...prev.skills, newSkill.trim()]
      }));
      setNewSkill('');
    }
  };

  const removeSkill = (skill: string) => {
    setCvData(prev => ({
      ...prev,
      skills: prev.skills.filter(s => s !== skill)
    }));
  };

  const addLanguage = () => {
    if (newLanguage.trim()) {
      setCvData(prev => ({
        ...prev,
        languages: [...prev.languages, newLanguage.trim()]
      }));
      setNewLanguage('');
    }
  };

  const removeLanguage = (language: string) => {
    setCvData(prev => ({
      ...prev,
      languages: prev.languages.filter(l => l !== language)
    }));
  };

  const addAchievement = () => {
    setCvData(prev => ({
      ...prev,
      achievements: [...prev.achievements, {
        id: Date.now().toString(),
        title: '',
        description: '',
        date: ''
      }]
    }));
  };

  const updateAchievement = (id: string, field: string, value: string) => {
    setCvData(prev => ({
      ...prev,
      achievements: prev.achievements.map(ach => 
        ach.id === id ? { ...ach, [field]: value } : ach
      )
    }));
  };

  const removeAchievement = (id: string) => {
    setCvData(prev => ({
      ...prev,
      achievements: prev.achievements.filter(ach => ach.id !== id)
    }));
  };

  const addProject = () => {
    setCvData(prev => ({
      ...prev,
      projects: [...prev.projects, {
        id: Date.now().toString(),
        name: '',
        description: '',
        technologies: '',
        link: ''
      }]
    }));
  };

  const updateProject = (id: string, field: string, value: string) => {
    setCvData(prev => ({
      ...prev,
      projects: prev.projects.map(proj => 
        proj.id === id ? { ...proj, [field]: value } : proj
      )
    }));
  };

  const removeProject = (id: string) => {
    setCvData(prev => ({
      ...prev,
      projects: prev.projects.filter(proj => proj.id !== id)
    }));
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-pink-500"></div>
      </div>
    );
  }

  // Render read-only CV view for other users
  if (isViewingOtherUser) {
    return (
      <div className="min-h-screen bg-gray-50">
        {/* Header */}
        <div className="bg-white border-b border-gray-200 px-4 py-3 flex items-center justify-between sticky top-0 z-10">
          <div className="flex items-center gap-3">
            <Link href={`/profile/${targetUserId}`}>
              <Button variant="ghost" size="sm">
                <ArrowLeft className="w-4 h-4" />
              </Button>
            </Link>
            <div className="flex items-center gap-2">
              <FileText className="w-5 h-5 text-blue-500" />
              <h1 className="font-semibold text-lg">CV Profile</h1>
            </div>
          </div>
        </div>

        <div className="p-4 max-w-4xl mx-auto space-y-6">
          {savedCV ? (
            <>
              {/* Personal Info */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <User className="w-5 h-5" />
                    Personal Information
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <h3 className="font-semibold text-gray-700">Full Name</h3>
                      <p className="text-gray-900">{savedCV.fullName || 'Not provided'}</p>
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-700">Account Type</h3>
                      <Badge variant="secondary">{savedCV.accountType}</Badge>
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-700">Email</h3>
                      <p className="text-gray-900">{savedCV.email || 'Not provided'}</p>
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-700">Phone</h3>
                      <p className="text-gray-900">{savedCV.phone || 'Not provided'}</p>
                    </div>
                    <div className="md:col-span-2">
                      <h3 className="font-semibold text-gray-700">Location</h3>
                      <p className="text-gray-900">{savedCV.location || 'Not provided'}</p>
                    </div>
                  </div>
                  {savedCV.summary && (
                    <div>
                      <h3 className="font-semibold text-gray-700">Professional Summary</h3>
                      <p className="text-gray-900 mt-2">{savedCV.summary}</p>
                    </div>
                  )}
                </CardContent>
              </Card>

              {/* Experience */}
              {savedCV.experience && savedCV.experience.length > 0 && (
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                      <Briefcase className="w-5 h-5" />
                      Work Experience
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    {savedCV.experience.map((exp: any) => (
                      <div key={exp.id} className="border-l-4 border-blue-500 pl-4">
                        <h3 className="font-semibold text-gray-900">{exp.position}</h3>
                        <p className="font-medium text-blue-600">{exp.company}</p>
                        <p className="text-sm text-gray-600">{exp.duration}</p>
                        {exp.description && <p className="text-gray-700 mt-2">{exp.description}</p>}
                      </div>
                    ))}
                  </CardContent>
                </Card>
              )}

              {/* Education */}
              {savedCV.education && savedCV.education.length > 0 && (
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                      <GraduationCap className="w-5 h-5" />
                      Education
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    {savedCV.education.map((edu: any) => (
                      <div key={edu.id} className="border-l-4 border-green-500 pl-4">
                        <h3 className="font-semibold text-gray-900">{edu.degree}</h3>
                        <p className="font-medium text-green-600">{edu.institution}</p>
                        <p className="text-sm text-gray-600">{edu.year}</p>
                        {edu.description && <p className="text-gray-700 mt-2">{edu.description}</p>}
                      </div>
                    ))}
                  </CardContent>
                </Card>
              )}

              {/* Skills */}
              {savedCV.skills && savedCV.skills.length > 0 && (
                <Card>
                  <CardHeader>
                    <CardTitle>Skills</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="flex flex-wrap gap-2">
                      {savedCV.skills.map((skill: string, index: number) => (
                        <Badge key={index} variant="outline">{skill}</Badge>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              )}

              {/* Languages */}
              {savedCV.languages && savedCV.languages.length > 0 && (
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                      <Languages className="w-5 h-5" />
                      Languages
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="flex flex-wrap gap-2">
                      {savedCV.languages.map((language: string, index: number) => (
                        <Badge key={index} variant="outline">{language}</Badge>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              )}

              {/* Projects */}
              {savedCV.projects && savedCV.projects.length > 0 && (
                <Card>
                  <CardHeader>
                    <CardTitle>Projects</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    {savedCV.projects.map((project: any) => (
                      <div key={project.id} className="border-l-4 border-purple-500 pl-4">
                        <h3 className="font-semibold text-gray-900">{project.name}</h3>
                        <p className="text-gray-700 mt-1">{project.description}</p>
                        {project.technologies && (
                          <p className="text-sm text-gray-600 mt-1">
                            <strong>Technologies:</strong> {project.technologies}
                          </p>
                        )}
                        {project.link && (
                          <a 
                            href={project.link} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            className="text-blue-600 hover:underline text-sm"
                          >
                            View Project
                          </a>
                        )}
                      </div>
                    ))}
                  </CardContent>
                </Card>
              )}

              {/* Achievements */}
              {savedCV.achievements && savedCV.achievements.length > 0 && (
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                      <Award className="w-5 h-5" />
                      Achievements
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    {savedCV.achievements.map((achievement: any) => (
                      <div key={achievement.id} className="border-l-4 border-yellow-500 pl-4">
                        <h3 className="font-semibold text-gray-900">{achievement.title}</h3>
                        <p className="text-sm text-gray-600">{achievement.date}</p>
                        <p className="text-gray-700 mt-1">{achievement.description}</p>
                      </div>
                    ))}
                  </CardContent>
                </Card>
              )}
            </>
          ) : (
            <Card>
              <CardContent className="text-center py-8">
                <FileText className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-semibold text-gray-900 mb-2">No CV Available</h3>
                <p className="text-gray-600">This user hasn't created a CV yet.</p>
              </CardContent>
            </Card>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 px-4 py-3 flex items-center justify-between sticky top-0 z-10">
        <div className="flex items-center gap-3">
          <Link href="/profile">
            <Button variant="ghost" size="sm">
              <ArrowLeft className="w-4 h-4" />
            </Button>
          </Link>
          <div className="flex items-center gap-2">
            <FileText className="w-5 h-5 text-blue-500" />
            <h1 className="font-semibold text-lg">My CV</h1>
          </div>
        </div>

        <Button 
          onClick={() => saveCVMutation.mutate(cvData)}
          disabled={saveCVMutation.isPending}
          className="bg-gradient-to-r from-pink-500 to-purple-600"
        >
          <Save className="w-4 h-4 mr-2" />
          Save CV
        </Button>
      </div>

      <div className="container mx-auto px-4 py-6 max-w-4xl">
        <div className="space-y-6">
          {/* Personal Information */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <User className="w-5 h-5" />
                Personal Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">Full Name</label>
                  <Input
                    value={cvData.fullName}
                    onChange={(e) => setCvData(prev => ({ ...prev, fullName: e.target.value }))}
                    placeholder="John Doe"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-2">Account Type</label>
                  <Select value={cvData.accountType} onValueChange={(value: any) => setCvData(prev => ({ ...prev, accountType: value }))}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="creator">Content Creator</SelectItem>
                      <SelectItem value="business">Business</SelectItem>
                      <SelectItem value="government">Government</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="block text-sm font-medium mb-2">Email</label>
                  <Input
                    type="email"
                    value={cvData.email}
                    onChange={(e) => setCvData(prev => ({ ...prev, email: e.target.value }))}
                    placeholder="john@example.com"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-2">Phone</label>
                  <Input
                    value={cvData.phone}
                    onChange={(e) => setCvData(prev => ({ ...prev, phone: e.target.value }))}
