client/src/pages/profile.tsx.content:
import { useState, useEffect } from "react";
import { useParams, useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuSeparator, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Navigation } from "@/components/layout/navigation";
import { Settings, Grid, Heart, MessageCircle, Share, Edit, Camera, Users, UserPlus, UserMinus, FileText, Menu, TrendingUp, User, Clock, HelpCircle, Info, LogOut, MessageSquare, CheckCircle, AtSign, Megaphone, Send, MoreHorizontal, Flag, UserX } from "lucide-react";
import { DMButton } from "@/components/ui/dm-button";
import { useToast } from "@/hooks/use-toast";

export default function ProfilePage() {
  const { userId } = useParams();
  const [, setLocation] = useLocation();
  const { user: currentUser } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showEditDialog, setShowEditDialog] = useState(false);
  const [editForm, setEditForm] = useState({
    bio: '',
    displayName: '',
    avatar: '',
    links: [] as { name: string; url: string }[]
  });
  const [uploading, setUploading] = useState(false);
  const [showFollowersList, setShowFollowersList] = useState(false);
  const [showFollowingList, setShowFollowingList] = useState(false);
  const [showMenuDropdown, setShowMenuDropdown] = useState(false);
  const [showServiceRequest, setShowServiceRequest] = useState(false);
  const [selectedService, setSelectedService] = useState('');
  const [serviceForm, setServiceForm] = useState({
    verifiedTickReason: '',
    customUsername: '',
    adBudget: '',
    adDescription: '',
    contactInfo: ''
  });
  const [showPerformanceStats, setShowPerformanceStats] = useState(false);
  const [showReportDialog, setShowReportDialog] = useState(false);
  const [reportForm, setReportForm] = useState({
    reason: '',
    message: ''
  });
  const [showDMDialog, setShowDMDialog] = useState(false);
  const [dmMessage, setDmMessage] = useState('');

  // Check if userId is a number or username
  const isNumericId = userId && !isNaN(parseInt(userId));
  const profileUserId = isNumericId ? parseInt(userId) : currentUser?.id;
  const profileUsername = !isNumericId ? userId : null;

  // Fetch user profile data - handle both username and userId
  const { data: profile, isLoading } = useQuery({
    queryKey: profileUsername ? [`/api/users/username/${profileUsername}`] : [`/api/users/${profileUserId}`],
    enabled: !!(profileUserId || profileUsername),
    retry: false,
  }) as { data: any, isLoading: boolean };

  // Determine if this is the current user's profile - using profile data when available
  const isOwnProfile = profile ? 
    profile.id === currentUser?.id : 
    (isNumericId ? 
      profileUserId === currentUser?.id : 
      profileUsername === currentUser?.username);

  // If profile not found but viewing own profile, use current user data
  const displayProfile = profile || (isOwnProfile ? currentUser : null);

  // Get the actual user ID for other queries (from profile data if we have it)
  const actualUserId = profile?.id || profileUserId;

  // Fetch user posts
  const { data: posts = [] } = useQuery({
    queryKey: [`/api/users/${actualUserId}/posts`],
    enabled: !!actualUserId,
  }) as { data: any[] };

  // Check if following
  const { data: isFollowing } = useQuery({
    queryKey: [`/api/users/${actualUserId}/following`],
    enabled: !!actualUserId && !isOwnProfile,
  }) as { data: boolean };

  // Fetch followers list
  const { data: followers = [] } = useQuery({
    queryKey: [`/api/users/${actualUserId}/followers`],
    enabled: !!profileUserId && showFollowersList,
  }) as { data: any[] };

  // Fetch following list
  const { data: following = [] } = useQuery({
    queryKey: [`/api/users/${profileUserId}/following-list`],
    enabled: !!profileUserId && showFollowingList,
  }) as { data: any[] };

  // Fetch CV data to check if user has meaningful CV content
  const { data: cvData } = useQuery({
    queryKey: isOwnProfile ? ['/api/cv'] : ['/api/users', profileUserId, 'cv'],
    queryFn: async () => {
      const endpoint = isOwnProfile ? '/api/cv' : `/api/users/${profileUserId}/cv`;
      const response = await apiRequest('GET', endpoint);
      return response.json();
    },
    enabled: !isOwnProfile, // Only check for other users
  });

  // Check if CV has meaningful content (more than just basic info)
  const hasMeaningfulCV = cvData && (
    (cvData.experience && cvData.experience.length > 0) ||
    (cvData.education && cvData.education.length > 0) ||
    (cvData.skills && cvData.skills.length > 0) ||
    (cvData.projects && cvData.projects.length > 0) ||
    (cvData.achievements && cvData.achievements.length > 0) ||
    (cvData.summary && cvData.summary.trim().length > 0)
  );

  // Report user mutation
  const reportUserMutation = useMutation({
    mutationFn: async () => {
      if (!reportForm.reason) {
        throw new Error('Please select a reason for reporting');
      }

      const response = await apiRequest('POST', `/api/users/${profileUserId}/report`, {
        reason: reportForm.reason,
        message: reportForm.message
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to submit report');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Report submitted",
        description: "Thank you for your report. We'll review it soon.",
      });
      setShowReportDialog(false);
      setReportForm({ reason: '', message: '' });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to submit report",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Fetch performance statistics
  const { data: performanceStats, isLoading: statsLoading } = useQuery({
    queryKey: isOwnProfile ? ['/api/performance-stats'] : ['/api/users', profileUserId, 'performance-stats'],
    queryFn: async () => {
      const endpoint = isOwnProfile ? '/api/performance-stats' : `/api/users/${profileUserId}/performance-stats`;
      const response = await apiRequest('GET', endpoint);
      return response.json();
    },
    enabled: showPerformanceStats,
  });

  // Follow/Unfollow mutation
  const followMutation = useMutation({
    mutationFn: async () => {
      const action = isFollowing ? 'unfollow' : 'follow';
      const sessionId = localStorage.getItem('sessionId');
      const response = await fetch(`/api/users/${profileUserId}/${action}`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${sessionId}`,
          'Content-Type': 'application/json'
        }
      });
      if (!response.ok) throw new Error('Failed to follow/unfollow');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/users/${profileUserId}/following`] });
      queryClient.invalidateQueries({ queryKey: [`/api/users/${profileUserId}`] });
      toast({
        title: isFollowing ? "Unfollowed" : "Following",
        description: `You ${isFollowing ? 'unfollowed' : 'are now following'} ${profile?.username}`,
      });
    },
  });

  // Send friend request mutation
  const friendRequestMutation = useMutation({
    mutationFn: async () => {
      const sessionId = localStorage.getItem('sessionId');
      const response = await fetch('/api/friend-requests', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${sessionId}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ toUserId: profileUserId }),
      });
      if (!response.ok) throw new Error('Failed to send friend request');
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Friend request sent",
        description: `Friend request sent to ${profile?.username}`,
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to send friend request",
        variant: "destructive",
      });
    }
  });

  // Update profile mutation
  const updateProfileMutation = useMutation({
    mutationFn: async (data: any) => {
      const sessionId = localStorage.getItem('sessionId');
      const response = await fetch(`/api/users/${currentUser?.id}`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${sessionId}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error('Failed to update profile');
      return response.json();
    },
    onSuccess: (updatedUser) => {
      // Update both profile queries with new data
      queryClient.setQueryData([`/api/users/${currentUser?.id}`], updatedUser);
      queryClient.setQueryData(['/api/me'], updatedUser);

      // Also invalidate to ensure fresh data on next load
      queryClient.invalidateQueries({ queryKey: [`/api/users/${currentUser?.id}`] });
      queryClient.invalidateQueries({ queryKey: ['/api/me'] });

      setShowEditDialog(false);
      toast({
        title: "Profile updated",
        description: "Your profile has been updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update profile",
        variant: "destructive",
      });
    }
  });

  const sendDMMutation = useMutation({
    mutationFn: async (message: string) => {
      return apiRequest('POST', '/api/message-requests', {
        toUserId: profileUserId,
        message: message
      });
    },
    onSuccess: () => {
      toast({
        title: "Message request sent",
        description: "Your message request has been sent.",
      });
      setShowDMDialog(false);
      setDmMessage('');
    },
    onError: (error: any) => {
      if (error.message.includes('Chat already exists')) {
        toast({
          title: "Chat exists",
          description: "You can already message this user. Redirecting to Messages...",
        });
        setShowDMDialog(false);
        setDmMessage('');
        setTimeout(() => {
          setLocation('/messages');
        }, 1000);
      } else {
        toast({
          title: "Failed to send message",
          description: error.message || "Something went wrong.",
          variant: "destructive",
        });
      }
    }
  });

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setUploading(true);
    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('/api/upload/avatar', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('sessionId')}`,
        },
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const result = await response.json();
      setEditForm({ ...editForm, avatar: result.url });

      // Update the profile immediately with new avatar
      queryClient.setQueryData([`/api/users/${currentUser?.id}`], (oldData: any) => ({
        ...oldData,
        avatar: result.url
      }));

      toast({
        title: "Image uploaded",
        description: "Profile picture uploaded successfully.",
      });
    } catch (error) {
      toast({
        title: "Upload failed",
        description: "Failed to upload image. Please try again.",
        variant: "destructive",
      });
    } finally {
      setUploading(false);
    }
  };

  useEffect(() => {
    if (profile) {
      let links = [];
      try {
        links = profile.links ? JSON.parse(profile.links) : [];
      } catch (e) {
        links = [];
      }

      setEditForm({
        bio: profile.bio || '',
        displayName: profile.displayName || '',
        avatar: profile.avatar || '',
        links: links
      });
    }
  }, [profile]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-pink-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white pb-nav">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 px-4 py-3 flex items-center justify-between sticky top-0 z-10">
        <div className="flex items-center">
          <h1 className="font-semibold text-lg text-left">{displayProfile?.username}</h1>
        </div>

{isOwnProfile ? (
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button
                variant="ghost"
                size="icon"
                className="rounded-full"
              >
                <Menu className="w-5 h-5" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end" className="w-56">
              <DropdownMenuItem onClick={() => {
                console.log('Account Center clicked from profile');
                setLocation('/account-center');
              }}>
                <User className="mr-2 h-4 w-4" />
                <span>Account Center</span>
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => {/* Time Management */}}>
                <Clock className="mr-2 h-4 w-4" />
                <span>Time Management</span>
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => setShowServiceRequest(true)}>
                <MessageSquare className="mr-2 h-4 w-4" />
                <span>Service Request</span>
              </DropdownMenuItem>
              <DropdownMenuSeparator />
              <DropdownMenuItem onClick={() => {/* Help and Support */}}>
                <HelpCircle className="mr-2 h-4 w-4" />
                <span>Help and Support</span>
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => {/* About */}}>
                <Info className="mr-2 h-4 w-4" />
                <span>About</span>
              </DropdownMenuItem>
              <DropdownMenuSeparator />
              <DropdownMenuItem 
                onClick={() => {
                  localStorage.removeItem('sessionId');
                  setLocation('/login');
                }}
                className="text-red-600"
              >
                <LogOut className="mr-2 h-4 w-4" />
                <span>Log Out</span>
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        ) : (
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button
                variant="ghost"
                size="icon"
                className="rounded-full"
              >
                <MoreHorizontal className="w-5 h-5" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end" className="w-48">
              <DropdownMenuItem onClick={() => setShowReportDialog(true)}>
                <Flag className="mr-2 h-4 w-4" />
                <span>Report User</span>
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => {/* Share Profile */
                const url = `${window.location.origin}/profile/${profileUserId}`;
                navigator.clipboard.writeText(url);
                toast({
                  title: "Profile link copied",
                  description: "The profile link has been copied to your clipboard.",
                });
              }}>
                <Share className="mr-2 h-4 w-4" />
                <span>Share Profile</span>
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => {
                const url = `${window.location.origin}/profile/${profileUserId}`;
                navigator.clipboard.writeText(url);
                toast({
                  title: "Profile URL copied",
                  description: "The profile URL has been copied to your clipboard.",
                });
              }}>
                <AtSign className="mr-2 h-4 w-4" />
                <span>Copy Profile URL</span>
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        )}
      </div>

      {/* Profile Header */}
      <div className="p-4">
        <div className="flex items-start space-x-4 mb-4">
          {/* Profile Picture */}
          <div className="relative">
            <Avatar className="w-20 h-20">
              <AvatarImage src={displayProfile?.avatar} alt={displayProfile?.username} />
              <AvatarFallback className="bg-gradient-to-r from-pink-500 to-purple-600 text-white text-xl">
                {displayProfile?.username?.[0]?.toUpperCase() || 'U'}
              </AvatarFallback>
            </Avatar>
          </div>

          {/* Stats */}
          <div className="flex-1">
            <div className="flex justify-around text-center">
              <div>
                <div className="font-semibold text-lg">{posts?.length || 0}</div>
                <div className="text-gray-500 text-sm">Posts</div>
              </div>
              <button
                onClick={() => setShowFollowersList(true)}
                className="hover:bg-gray-50 rounded-lg p-2 transition-colors"
              >
                <div className="font-semibold text-lg">{displayProfile?.followersCount || 0}</div>
                <div className="text-gray-500 text-sm">Followers</div>
              </button>
              <button
                onClick={() => setShowFollowingList(true)}
                className="hover:bg-gray-50 rounded-lg p-2 transition-colors"
              >
                <div className="font-semibold text-lg">{displayProfile?.followingCount || 0}</div>
                <div className="text-gray-500 text-sm">Following</div>
              </button>
            </div>
          </div>
        </div>

        {/* Bio */}
        <div className="mb-4">
          <div className="mb-2">
            <div className="flex items-center gap-4 mb-2">
              <h2 className="font-semibold text-base">{displayProfile?.displayName || displayProfile?.username}</h2>

              {/* Platform Links */}
              <div className="flex gap-2">
                {(() => {
                  try {
                    const links = displayProfile?.links ? JSON.parse(displayProfile.links) : [];
                    return links.slice(0, 2).map((link: any, index: number) => (
                      <button
                        key={index}
                        onClick={() => {
                          const url = link.url.startsWith('http') ? link.url : `https://${link.url}`;
                          window.open(url, '_blank', 'noopener,noreferrer');
                        }}
                        className="bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-xs font-medium hover:bg-blue-200 transition-colors"
                      >
                        {link.name}
                      </button>
                    ));
                  } catch (e) {
                    return null;
                  }
                })()}
              </div>
            </div>
          </div>

          {displayProfile?.bio && (
            <p className="text-gray-700 text-sm mt-1 whitespace-pre-wrap">{displayProfile.bio}</p>
          )}


        </div>

        {/* Action Buttons */}
        <div className="flex gap-1">
          {isOwnProfile ? (
            <>
              <Button
                variant="outline"
                className="flex-1 text-xs px-1 py-2 min-w-0"
                onClick={() => setShowEditDialog(true)}
              >
                <Edit className="w-3 h-3 mr-1" />
                <span className="truncate">Edit</span>
              </Button>
              <Button
                variant="outline"
                className="flex-1 text-xs px-1 py-2 min-w-0"
                onClick={() => setLocation('/cv')}
              >
                <FileText className="w-3 h-3 mr-1" />
                <span className="truncate">CV</span>
              </Button>
              <Button
                variant="outline"
                className="flex-1 text-xs px-1 py-2 min-w-0"
                onClick={() => setShowPerformanceStats(!showPerformanceStats)}
              >
                <TrendingUp className="w-3 h-3 mr-1" />
                <span className="truncate">Performance</span>
              </Button>
            </>
          ) : (
            <>
              <Button
                variant={isFollowing ? "outline" : "default"}
                className="flex-1 text-sm px-2 py-2"
                onClick={() => followMutation.mutate()}
                disabled={followMutation.isPending}
              >
                {isFollowing ? (
                  <>
                    <UserMinus className="w-4 h-4 mr-2" />
                    Unfollow
                  </>
                ) : (
                  <>
                    <UserPlus className="w-4 h-4 mr-2" />
                    Follow
                  </>
