      {/* DM Dialog */}
      <Dialog open={showDMDialog} onOpenChange={setShowDMDialog}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Send Message Request</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Message to {displayProfile?.displayName || displayProfile?.username}
              </label>
              <Textarea
                placeholder="Write your message..."
                value={dmMessage}
                onChange={(e) => setDmMessage(e.target.value)}
                className="min-h-[100px]"
                maxLength={500}
              />
              <div className="text-xs text-gray-500 mt-1">
                {dmMessage.length}/500 characters
              </div>
            </div>
          </div>

          <div className="flex space-x-2 pt-4 border-t">
            <Button
              variant="outline"
              className="flex-1"
              onClick={() => {
                setShowDMDialog(false);
                setDmMessage('');
              }}
            >
              Cancel
            </Button>
            <Button
              className="flex-1 bg-gradient-to-r from-pink-500 to-purple-600"
              onClick={() => sendDMMutation.mutate(dmMessage)}
              disabled={sendDMMutation.isPending || !dmMessage.trim()}
            >
              <Send className="w-4 h-4 mr-2" />
              {sendDMMutation.isPending ? "Sending..." : "Send Request"}
            </Button>
          </div>
        </DialogContent>
      </Dialog>

      {/* Report User Dialog */}
      <Dialog open={showReportDialog} onOpenChange={setShowReportDialog}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Report User</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Reason for reporting
              </label>
              <select
                value={reportForm.reason}
                onChange={(e) => setReportForm({ ...reportForm, reason: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-pink-500"
                required
              >
                <option value="">Select a reason</option>
                <option value="spam">Spam</option>
                <option value="harassment">Harassment</option>
                <option value="inappropriate_content">Inappropriate Content</option>
                <option value="fake_account">Fake Account</option>
                <option value="violence">Violence or Threats</option>
                <option value="hate_speech">Hate Speech</option>
                <option value="copyright">Copyright Violation</option>
                <option value="other">Other</option>
              </select>
            </div>
          </div>

          <div className="flex space-x-2 pt-4 border-t">
            <Button
              variant="outline"
              className="flex-1"
              onClick={() => {
                setShowReportDialog(false);
                setReportForm({ reason: '', message: '' });
              }}
            >
              Cancel
            </Button>
            <Button
              className="flex-1 bg-red-600 hover:bg-red-700"
              onClick={() => reportUserMutation.mutate()}
              disabled={reportUserMutation.isPending || !reportForm.reason}
            >
              {reportUserMutation.isPending ? "Submitting..." : "Submit Report"}
            </Button>
          </div>
        </DialogContent>
      </Dialog>

      {/* Bottom Navigation */}
      <Navigation />
    </div>
  );
}

