import { useState, useRef, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Navigation } from "@/components/layout/navigation";
import { useAuth } from "@/hooks/use-auth";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import { Heart, MessageCircle, Share, Link as LinkIcon, MoreVertical, Volume2, VolumeX, Copy, BookmarkPlus, ThumbsUp, ThumbsDown, Users, Flag } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { CommentModal } from "@/components/post/comment-modal";
import Auth from "@/pages/auth";
import { InterstitialAd, useInterstitialAd } from "@/components/ads/interstitial-ad";
import { useLocation } from "wouter";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
  DropdownMenuSeparator,
} from "@/components/ui/dropdown-menu";

export default function Trends() {
  const { isAuthenticated, user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [, setLocation] = useLocation();
  const [videoMuteStates, setVideoMuteStates] = useState<Map<number, boolean>>(new Map());
  const [followingUsers, setFollowingUsers] = useState(new Set<number>());
  const [commentModalOpen, setCommentModalOpen] = useState(false);
  const [selectedPostId, setSelectedPostId] = useState<number | null>(null);
  const [currentPlayingVideo, setCurrentPlayingVideo] = useState<number | null>(null);
  const videoRefs = useRef<Map<number, HTMLVideoElement>>(new Map());
  const tapTimeouts = useRef<Map<number, number>>(new Map());

  // Get postId from URL parameters for direct navigation
  const urlParams = new URLSearchParams(window.location.search);
  const targetPostId = urlParams.get('postId') ? Number(urlParams.get('postId')) : null;

  const [touchStart, setTouchStart] = useState<{ x: number; y: number; time: number } | null>(null);
  const [shareMenuOpen, setShareMenuOpen] = useState<number | null>(null);
  const [savedPosts, setSavedPosts] = useState<Set<number>>(new Set());
  const [interestedPosts, setInterestedPosts] = useState<Set<number>>(new Set());
  const [notInterestedPosts, setNotInterestedPosts] = useState<Set<number>>(new Set());

  // Ad management - show interstitial ad every 5 videos
  const { showAd, triggerAd, closeAd } = useInterstitialAd(5);

  const { data: posts = [], isLoading } = useQuery({
    queryKey: ["/api/posts", "videos"],
    queryFn: async () => {
      // Get only video posts from users (not admin posts)
      const response = await fetch("/api/posts?adminOnly=false", {
        headers: isAuthenticated ? {
          'Authorization': `Bearer ${localStorage.getItem('sessionId')}`
        } : {}
      });
      if (!response.ok) throw new Error('Failed to fetch trends');
      const allPosts = await response.json();
      // Filter only video posts
      return allPosts.filter((post: any) => post.videoUrl);
    },
  });

  // Fetch user's following list to maintain persistent follow state
  const { data: followingList = [] } = useQuery({
    queryKey: ["/api/friends/status"],
    queryFn: async () => {
      if (!isAuthenticated || !user?.id) return [];
      const response = await fetch(`/api/friends/status`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('sessionId')}`
        }
      });
      if (!response.ok) return [];
      return response.json();
    },
    enabled: isAuthenticated && !!user?.id
  });

  // Update following state when data is fetched
  useEffect(() => {
    if (followingList.length > 0) {
      const followingIds = new Set<number>(followingList.map((user: any) => Number(user.id)));
      setFollowingUsers(followingIds);
    }
  }, [followingList]);

  const reportPostMutation = useMutation({
    mutationFn: async (postId: number) => {
      try {
        const response = await fetch(`/api/posts/${postId}/report`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.getItem('sessionId')}`
          },
          body: JSON.stringify({ reason: 'Inappropriate content' })
        });

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({ message: 'Unknown error' }));
          throw new Error(errorData.message || `HTTP ${response.status}`);
        }

        return response.json();
      } catch (error) {
        console.error('Report post error:', error);
        throw error;
      }
    },
    onSuccess: () => {
      toast({
        title: "Post reported",
        description: "Thank you for your report. We'll review this content.",
      });
    },
    onError: (error: any) => {
      console.error('Report mutation error:', error);
      toast({
        title: "Error",
        description: error?.message || "Failed to report post. Please try again.",
        variant: "destructive",
      });
    }
  });

  const likeMutation = useMutation({
    mutationFn: async (postId: number) => {
      const response = await fetch(`/api/posts/${postId}/like`, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("sessionId")}`,
        },
      });
      if (!response.ok) throw new Error('Failed to like post');
      return response.json();
    },
    onSuccess: (data, postId) => {
      // Update post in cache with new like count
      queryClient.setQueryData(["/api/posts", "videos"], (oldPosts: any[]) => {
        if (!oldPosts) return oldPosts;
        return oldPosts.map(p => 
          p.id === postId 
            ? { ...p, likesCount: data.likesCount, isLiked: data.liked }
            : p
        );
      });
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
    },
  });

  // Instagram-style reels autoplay with intersection observer
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          const videoElement = entry.target.querySelector('video');
          const postId = parseInt(entry.target.getAttribute('data-post-id') || '0');

          if (!videoElement || !postId) return;

          if (entry.isIntersecting) {
            // Pause all other videos first
            videoRefs.current.forEach((v, id) => {
              if (id !== postId && v) {
                v.pause();
                v.currentTime = 0;
                v.volume = 0;
                v.muted = true;
              }
            });

            // Set current playing video
            setCurrentPlayingVideo(postId);

            // Auto-play the visible video
            videoElement.currentTime = 0;
            videoElement.muted = videoMuteStates.get(postId) ?? false;
            videoElement.volume = videoElement.muted ? 0 : 1;

            // For first video, try unmuted autoplay
            if (videoMuteStates.get(postId) === undefined) {
              videoElement.muted = false;
              videoElement.volume = 1;
              setVideoMuteStates(prev => new Map(prev.set(postId, false)));
            }

            videoElement
              .play()
              .then(() => {
                console.log(`Instagram-style autoplay: video ${postId} ${videoElement.muted ? '(muted)' : '(with sound)'}`);
              })
              .catch((err) => {
                // Fallback to muted autoplay
                console.log(`Autoplay blocked for video ${postId}, trying muted:`, err);
                videoElement.muted = true;
                videoElement.volume = 0;
                setVideoMuteStates(prev => new Map(prev.set(postId, true)));
                videoElement.play().catch((retryErr) => {
                  console.warn(`Failed to autoplay video ${postId} even muted:`, retryErr);
                });
              });
          } else {
            // Pause and reset when out of view
            videoElement.pause();
            videoElement.currentTime = 0;
            videoElement.volume = 0;
            videoElement.muted = true;

            if (currentPlayingVideo === postId) {
              console.log(`Paused video ${postId} - out of view`);
              setCurrentPlayingVideo(null);
              // Trigger ad check when video changes (Instagram Reels style)
              triggerAd();
            }
          }
        });
      },
      {
        threshold: 0.75, // Only when 75% is visible (Instagram-like)
        rootMargin: '0px'
      }
    );

    // Observe all video containers
    const containers = document.querySelectorAll('[data-post-id]');
    containers.forEach((container) => {
      observer.observe(container);
    });

    return () => {
      containers.forEach((container) => {
        observer.unobserve(container);
      });
    };
  }, [currentPlayingVideo, videoMuteStates]);

  // Cleanup effect for component unmount
  useEffect(() => {
    return () => {
      // Pause all videos and reset on unmount
      videoRefs.current.forEach(video => {
        if (video) {
          video.pause();
          video.currentTime = 0;
        }
      });
      videoRefs.current.clear();
      tapTimeouts.current.forEach(timeout => clearTimeout(timeout));
      tapTimeouts.current.clear();
    };
  }, []);

  // Simple and reliable tap detection for mobile
  const handleVideoClick = (postId: number, event: React.MouseEvent) => {
    if (!isAuthenticated) {
      toast({
        title: "Please login",
        description: "You need to login to interact with videos",
        variant: "destructive"
      });
      return;
    }

    event.preventDefault();
    event.stopPropagation();

    // Always toggle mute on single click
    toggleVideoMute(postId);
  };

  const handleVideoDoubleClick = (postId: number, event: React.MouseEvent) => {
    if (!isAuthenticated) {
      toast({
        title: "Please login", 
        description: "You need to login to interact with videos",
        variant: "destructive"
      });
      return;
    }

    event.preventDefault();
    event.stopPropagation();

    // Like the video on double click
    likeMutation.mutate(postId);

    // Visual feedback for double tap
    const target = event.currentTarget as HTMLElement;
    const heart = document.createElement('div');
    heart.innerHTML = 'â¤ï¸';
    heart.style.cssText = `
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      font-size: 60px;
      pointer-events: none;
      z-index: 1000;
      animation: heartPop 0.6s ease-out forwards;
    `;

    // Add keyframe animation if not exists
    if (!document.querySelector('#heartAnimation')) {
      const style = document.createElement('style');
      style.id = 'heartAnimation';
      style.textContent = `
        @keyframes heartPop {
          0% { transform: translate(-50%, -50%) scale(0); opacity: 1; }
          50% { transform: translate(-50%, -50%) scale(1.2); opacity: 1; }
          100% { transform: translate(-50%, -50%) scale(1.5); opacity: 0; }
        }
      `;
      document.head.appendChild(style);
    }

    target.appendChild(heart);
    setTimeout(() => heart.remove(), 600);
  };

  const toggleVideoMute = (postId: number) => {
    const video = videoRefs.current.get(postId);
    if (video) {
      const currentMute = videoMuteStates.get(postId) ?? false;
      const newMute = !currentMute;
      video.muted = newMute;
      setVideoMuteStates(prev => new Map(prev.set(postId, newMute)));

      toast({
        title: newMute ? "Video muted" : "Video unmuted",
        duration: 1000
      });
    }
  };

  // Share functionality
  const copyVideoLink = (postId: number) => {
    const link = `${window.location.origin}/trends?video=${postId}`;
    navigator.clipboard.writeText(link).then(() => {
      toast({
        title: "Link copied to clipboard",
        duration: 2000
      });
    });
  };

  const shareToCircle = (postId: number) => {
    window.location.href = `/chats?shareVideo=${postId}`;
  };

  const addToCircleVibe = (postId: number) => {
    window.location.href = `/circle/add-vibe?video=${postId}`;
  };

  const shareToExternalApp = (postId: number) => {
    const link = `${window.location.origin}/trends?video=${postId}`;
    if (navigator.share) {
      navigator.share({
        title: 'Check out this video on TrendoTalk',
        url: link
      });
    } else {
      copyVideoLink(postId);
    }
  };

  // Interaction functions
  const toggleSavePost = (postId: number) => {
    setSavedPosts(prev => {
      const newSet = new Set(prev);
      if (newSet.has(postId)) {
        newSet.delete(postId);
        toast({ title: "Removed from saved", duration: 1500 });
      } else {
        newSet.add(postId);
        toast({ title: "Saved to collection", duration: 1500 });
      }
      return newSet;
    });
  };

  const markInterested = (postId: number) => {
    setInterestedPosts(prev => new Set(prev.add(postId)));
    setNotInterestedPosts(prev => {
      const newSet = new Set(prev);
      newSet.delete(postId);
      return newSet;
    });
    toast({ title: "Marked as interested", duration: 1500 });
  };

  const markNotInterested = (postId: number) => {
    setNotInterestedPosts(prev => new Set(prev.add(postId)));
    setInterestedPosts(prev => {
      const newSet = new Set(prev);
      newSet.delete(postId);
      return newSet;
    });
    toast({ title: "Marked as not interested", duration: 1500 });
  };

  const followMutation = useMutation({
    mutationFn: async ({ userId, action }: { userId: number; action: 'follow' | 'unfollow' }) => {
      const method = action === 'unfollow' ? 'DELETE' : 'POST';
      const response = await fetch(`/api/users/${userId}/follow`, {
        method,
        headers: {
          Authorization: `Bearer ${localStorage.getItem("sessionId")}`,
        },
      });
      if (!response.ok) throw new Error('Failed to follow/unfollow user');
      return response.json();
    },
    onSuccess: (data, variables) => {
      setFollowingUsers(prev => {
        const newSet = new Set(prev);
        if (variables.action === 'follow') {
          newSet.add(variables.userId);
        } else {
          newSet.delete(variables.userId);
        }
        return newSet;
      });
      // Invalidate both posts and following list to ensure persistent state
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
      queryClient.invalidateQueries({ queryKey: ["/api/friends/status"] });
    },
  });

  const handleComment = (postId: number) => {
    setSelectedPostId(postId);
    setCommentModalOpen(true);
  };

  const handleFollow = (userId: number) => {
    const isFollowing = followingUsers.has(userId);
    followMutation.mutate({
      userId,
      action: isFollowing ? 'unfollow' : 'follow'
    });

    // Update local state immediately for better UX
    setFollowingUsers(prev => {
      const newSet = new Set(prev);
      if (isFollowing) {
        newSet.delete(userId);
      } else {
        newSet.add(userId);
      }
      return newSet;
    });
  };

  // Auto-scroll to specific post when postId is in URL
  useEffect(() => {
    if (targetPostId && posts.length > 0) {
      const targetIndex = posts.findIndex((post: any) => post.id === targetPostId);
      if (targetIndex !== -1) {
        const element = document.getElementById(`post-${targetPostId}`);
        if (element) {
          element.scrollIntoView({ behavior: 'smooth', block: 'center' });
          // Clear URL parameter after navigation
          window.history.replaceState({}, '', '/trends');
        }
      }
    }
  }, [targetPostId, posts]);

  if (!isAuthenticated) {
    return <Auth />;
  }

  return (
    <div className="min-h-screen bg-black relative">
      {/* Header */}
      <div className="absolute top-0 left-0 right-0 z-50 p-4">
        <h1 className="text-white text-xl font-bold">Trends</h1>
      </div>

      {/* Video Feed */}
      <div className="h-screen overflow-y-auto snap-y snap-mandatory scrollbar-hide pb-nav" style={{ scrollBehavior: 'smooth', scrollSnapType: 'y mandatory' }}>
        {isLoading ? (
          <div className="h-screen flex items-center justify-center">
            <div className="text-white">Loading trends...</div>
          </div>
        ) : posts.length === 0 ? (
          <div className="h-screen flex items-center justify-center text-center px-4">
            <div>
              <h3 className="text-white text-lg font-semibold mb-2">No video trends yet</h3>
              <p className="text-gray-400">Be the first to upload a video trend!</p>
            </div>
          </div>
        ) : (
          posts.map((post: any, index: number) => (
            <div 
              key={post.id}
              id={`post-${post.id}`}
              className="relative h-[100vh] snap-start snap-always overflow-hidden"
              data-post-id={post.id}
              style={{ scrollSnapAlign: 'start', scrollSnapStop: 'always' }}

            >
              {/* Video Background with tap controls */}
              <video
                ref={(el) => {
                  if (el) {
                    videoRefs.current.set(post.id, el);
                    // Set initial mute state to false (unmuted) for trends section
                    if (!videoMuteStates.has(post.id)) {
                      el.muted = false;
                      setVideoMuteStates(prev => new Map(prev.set(post.id, false)));
                    }
                  } else {
                    videoRefs.current.delete(post.id);
                  }
                }}
                src={post.videoUrl}
                className="absolute inset-0 w-full h-full object-cover cursor-pointer"
                muted={videoMuteStates.get(post.id) ?? false}
                loop
                playsInline
                preload="auto"
                onLoadedData={() => {
                  // Ensure video is ready for Instagram-style autoplay
                  const video = videoRefs.current.get(post.id);
                  if (video) {
                    video.muted = true;
                    video.playsInline = true;
                    video.loop = true;
                  }
                }}
                onCanPlay={() => {
                  // Additional trigger when video can play
                  const video = videoRefs.current.get(post.id);
                  if (video && currentPlayingVideo === post.id && video.paused) {
                    video.play().catch(() => {
                      video.muted = true;
                      video.play().catch(console.error);
                    });
                  }
                }}
                onClick={(e) => handleVideoClick(post.id, e)}
                onDoubleClick={(e) => handleVideoDoubleClick(post.id, e)}
                onTouchStart={(e: React.TouchEvent) => {
                  e.preventDefault();
                  const touch = e.touches[0];
                  setTouchStart({
                    x: touch.clientX,
                    y: touch.clientY,
                    time: Date.now()
                  });
                }}
                onTouchEnd={(e: React.TouchEvent) => {
                  e.preventDefault();

                  if (!isAuthenticated) {
                    toast({
                      title: "Please login",
                      description: "You need to login to interact with videos",
                      variant: "destructive"
                    });
                    return;
                  }

                  if (!touchStart) return;

                  const touch = e.changedTouches[0];
                  const deltaX = touch.clientX - touchStart.x;
                  const deltaY = touch.clientY - touchStart.y;
                  const deltaTime = Date.now() - touchStart.time;
                  const distance = Math.sqrt(deltaX * deltaX + deltaY * deltaY);

                  // Swipe right to share
                  if (deltaX > 120 && Math.abs(deltaY) < 80 && deltaTime < 600) {
                    window.location.href = `/chats?shareVideo=${post.id}`;
                    setTouchStart(null);
                    return;
                  }

                  // Tap detection
                  if (distance < 50 && deltaTime < 400) {
                    const lastTap = tapTimeouts.current.get(post.id) || 0;
                    const now = Date.now();

                    if (now - lastTap < 400) {
                      // Double tap - like
                      likeMutation.mutate(post.id);
                      tapTimeouts.current.delete(post.id);

                      // Heart animation
                      const videoElement = e.currentTarget as HTMLElement;
                      const heart = document.createElement('div');
                      heart.innerHTML = 'â¤ï¸';
                      heart.style.cssText = `
                        position: absolute;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%) scale(0);
                        font-size: 4rem;
                        pointer-events: none;
                        z-index: 9999;
                        transition: all 0.3s ease;
                      `;
                      videoElement.appendChild(heart);

                      requestAnimationFrame(() => {
                        heart.style.transform = 'translate(-50%, -50%) scale(1.2)';
                        heart.style.opacity = '1';
                      });

                      setTimeout(() => {
                        heart.style.transform = 'translate(-50%, -50%) scale(0)';
                        heart.style.opacity = '0';
                        setTimeout(() => heart.remove(), 300);
                      }, 400);
                    } else {
                      // Single tap - mute/unmute
                      tapTimeouts.current.set(post.id, now);
                      setTimeout(() => {
                        if (tapTimeouts.current.get(post.id) === now) {
                          toggleVideoMute(post.id);
                        }
                      }, 400);
                    }
                  }

                  setTouchStart(null);
                }}
              />

              {/* Centered mute button for single tap */}
              <div className="absolute inset-0 flex items-center justify-center z-20 pointer-events-none">
                <Button
                  variant="ghost"
                  size="lg"
                  className="w-16 h-16 rounded-full bg-black/40 text-white hover:bg-black/60 p-0 pointer-events-auto opacity-70 hover:opacity-100 transition-all duration-200"
                  onClick={(e) => {
                    e.stopPropagation();
                    toggleVideoMute(post.id);
                  }}
                >
                  {videoMuteStates.get(post.id) ?? false ? <VolumeX className="w-8 h-8" /> : <Volume2 className="w-8 h-8" />}
                </Button>
              </div>

              {/* Gradient Overlay */}
              <div className="absolute inset-0 bg-gradient-to-t from-black/70 via-transparent to-black/30" />

              {/* Right Side Actions */}
              <div className="absolute right-3 bottom-20 flex flex-col items-center space-y-6">
                {/* Like Button */}
                <div className="flex flex-col items-center">
                  <Button
                    variant="ghost"
                    size="sm"
                    className="w-12 h-12 rounded-full text-white hover:bg-white/20 p-0"
                    onClick={() => likeMutation.mutate(post.id)}
                  >
                    <Heart className={`w-7 h-7 ${post.isLiked ? 'fill-red-500 text-red-500' : ''}`} />
                  </Button>
                  <span className="text-white text-xs font-semibold mt-1">
                    {post.likesCount > 999 
                      ? `${(post.likesCount / 1000).toFixed(1)}k` 
                      : post.likesCount || 0
                    }
                  </span>
                </div>

                {/* Comment Button */}
                <div className="flex flex-col items-center">
                  <Button
                    variant="ghost"
                    size="sm"
                    className="w-12 h-12 rounded-full text-white hover:bg-white/20 p-0"
                    onClick={() => handleComment(post.id)}
                  >
                    <MessageCircle className="w-7 h-7" />
                  </Button>
                  <span className="text-white text-xs font-semibold mt-1">
                    {post.commentsCount > 999 
                      ? `${(post.commentsCount / 1000).toFixed(1)}k` 
                      : post.commentsCount || 0
                    }
                  </span>
                </div>

                {/* Share Button with Dropdown */}
                <div className="flex flex-col items-center">
                  <DropdownMenu open={shareMenuOpen === post.id} onOpenChange={(open) => setShareMenuOpen(open ? post.id : null)}>
                    <DropdownMenuTrigger asChild>
                      <Button
                        variant="ghost"
                        size="sm"
                        className="w-12 h-12 rounded-full text-white hover:bg-white/20 p-0"
                      >
                        <Share className="w-6 h-6" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent className="w-56 bg-black/90 border-gray-700 text-white">
                      <DropdownMenuItem onClick={() => copyVideoLink(post.id)} className="hover:bg-white/10">
                        <Copy className="w-4 h-4 mr-2" />
                        Copy link
                      </DropdownMenuItem>
                      <DropdownMenuItem onClick={() => shareToCircle(post.id)} className="hover:bg-white/10">
                        <Users className="w-4 h-4 mr-2" />
                        Share to Circle
                      </DropdownMenuItem>
                      <DropdownMenuItem onClick={() => addToCircleVibe(post.id)} className="hover:bg-white/10">
                        <Users className="w-4 h-4 mr-2" />
                        Add to Circle vibe
                      </DropdownMenuItem>
                      <DropdownMenuItem onClick={() => shareToExternalApp(post.id)} className="hover:bg-white/10">
                        <Share className="w-4 h-4 mr-2" />
                        Share to other apps
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                  <span className="text-white text-xs font-semibold mt-1">Share</span>
                </div>

                {/* Link Button (if link exists) */}
                {post.link && (
                  <div className="flex flex-col items-center">
                    <Button
                      variant="ghost"
                      size="sm"
                      className="w-12 h-12 rounded-full text-white hover:bg-white/20 p-0"
                      onClick={() => window.open(post.link, '_blank')}
                    >
                      <LinkIcon className="w-6 h-6" />
                    </Button>
                  </div>
                )}

                {/* More Options with Three-Dot Menu */}
                <div className="flex flex-col items-center">
                  <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                      <Button
                        variant="ghost"
                        size="sm"
                        className="w-12 h-12 rounded-full text-white hover:bg-white/20 p-0"
                      >
                        <MoreVertical className="w-6 h-6" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent className="w-56 bg-black/90 border-gray-700 text-white">
                      <DropdownMenuItem onClick={() => toggleSavePost(post.id)} className="hover:bg-white/10">
                        <BookmarkPlus className="w-4 h-4 mr-2" />
                        {savedPosts.has(post.id) ? 'Remove from saved' : 'Save to collection'}
                      </DropdownMenuItem>
                      <DropdownMenuSeparator className="bg-gray-600" />
                      <DropdownMenuItem onClick={() => markInterested(post.id)} className="hover:bg-white/10">
                        <ThumbsUp className="w-4 h-4 mr-2" />
                        Interested
                      </DropdownMenuItem>
                      <DropdownMenuItem onClick={() => markNotInterested(post.id)} className="hover:bg-white/10">
                        <ThumbsDown className="w-4 h-4 mr-2" />
                        Not interested
                      </DropdownMenuItem>
                      <DropdownMenuSeparator className="bg-gray-600" />
                      <DropdownMenuItem onClick={() => reportPostMutation.mutate(post.id)} className="hover:bg-red-600/20 text-red-400">
                        <Flag className="w-4 h-4 mr-2" />
                        Report this post
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                </div>
              </div>

              {/* Bottom Content */}
              <div className="absolute bottom-16 left-4 right-20">
                {/* Username, Avatar and Follow Button */}
                <div className="flex items-center space-x-3 mb-4">
                  <Avatar className="w-8 h-8 ring-2 ring-white">
                    <AvatarImage src={post.user.avatar} alt={post.user.username} />
                    <AvatarFallback className="bg-gradient-to-r from-pink-500 to-purple-600 text-white text-xs">
                      {post.user.username[3]?.toUpperCase()}
                    </AvatarFallback>
                  </Avatar>
                  <button 
                    className="text-white font-bold text-base hover:text-purple-300 transition-colors text-left"
                    onClick={() => setLocation(`/profile/${post.user.id}`)}
                  >
                    {post.user.username}
                  </button>
                  <Button 
                    className={`px-3 py-1 text-xs font-semibold rounded-md border transition-colors ${
                      followingUsers.has(post.user.id) 
                        ? 'bg-gray-600 border-gray-600 text-white hover:bg-gray-700' 
                        : 'bg-transparent border-white text-white hover:bg-white hover:text-black'
                    }`}
                    onClick={() => handleFollow(post.user.id)}
                  >
                    {followingUsers.has(post.user.id) ? 'Following' : 'Follow'}
                  </Button>
                </div>

                {/* Caption */}
                <p className="text-white text-sm mb-3 line-clamp-2">{post.caption}</p>

                {/* Link indicator */}
                {post.link && (
                  <div className="flex items-center space-x-2 mt-2">
                    <div className="w-4 h-4 bg-white rounded-sm flex items-center justify-center">
                      <LinkIcon className="w-3 h-3 text-black" />
                    </div>
                    <span className="text-white text-xs">Link attached</span>
                  </div>
                )}
              </div>
            </div>
          ))
        )}
      </div>

      {/* Fullscreen Interstitial Ad - Instagram Reels style */}
      <InterstitialAd
        isOpen={showAd}
        onClose={closeAd}
        showSkipAfter={5}
        autoClose={true}
        autoCloseAfter={10}
      />

      <Navigation />

      {/* Comment Modal */}
      {selectedPostId && (
        <CommentModal 
          isOpen={commentModalOpen}
          onClose={() => {
            setCommentModalOpen(false);
            setSelectedPostId(null);
          }}
          postId={selectedPostId}
          currentUser={user}
        />
      )}
    </div>
  );
}

