import { useState, useEffect, useRef } from 'react';
import { Button } from '@/components/ui/button';
import { Avatar, AvatarImage, AvatarFallback } from '@/components/ui/avatar';
import { Phone, PhoneOff, Mic, MicOff, Volume2, VolumeX, Minimize2 } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

interface CallInterfaceProps {
  isIncoming: boolean;
  caller: {
    username: string;
    avatar?: string;
  };
  onAccept: () => void;
  onDecline: () => void;
  onEndCall: () => void;
  callStatus: 'incoming' | 'connecting' | 'connected' | 'ended';
  duration?: number;
  onToggleMute?: () => boolean;
  isMuted?: boolean;
  onMinimize?: () => void;
}

export function CallInterface({ 
  isIncoming, 
  caller, 
  onAccept, 
  onDecline, 
  onEndCall, 
  callStatus,
  duration = 0,
  onToggleMute,
  isMuted = false,
  onMinimize
}: CallInterfaceProps) {
  const [isSpeakerOn, setIsSpeakerOn] = useState(false);
  const { toast } = useToast();

  const formatDuration = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const handleMute = () => {
    if (onToggleMute) {
      const newMutedState = onToggleMute();
      toast({
        title: newMutedState ? "Microphone off" : "Microphone on",
        description: newMutedState ? "You are now muted" : "You can now speak",
      });
    }
  };

  const handleSpeaker = () => {
    setIsSpeakerOn(!isSpeakerOn);
    toast({
      title: isSpeakerOn ? "Speaker off" : "Speaker on",
      description: isSpeakerOn ? "Audio through earpiece" : "Audio through speaker",
    });
  };

  if (callStatus === 'ended') {
    return null;
  }

  return (
    <div className="fixed inset-0 bg-gradient-to-br from-pink-500 via-purple-600 to-blue-600 flex flex-col items-center justify-center z-50 text-white">
      {/* Minimize button - only show for active calls, not incoming */}
      {callStatus !== 'incoming' && onMinimize && (
        <div className="absolute top-6 left-6">
          <Button
            onClick={onMinimize}
            variant="ghost"
            size="sm"
            className="text-white hover:bg-white/20 w-10 h-10 p-0"
          >
            <Minimize2 className="w-5 h-5" />
          </Button>
        </div>
      )}

      {/* Caller Info */}
      <div className="text-center mb-8">
        <Avatar className="w-32 h-32 mx-auto mb-4 ring-4 ring-white/20">
          <AvatarImage src={caller.avatar} alt={caller.username} />
          <AvatarFallback className="bg-white/20 text-white text-2xl">
            {caller.username?.[0]?.toUpperCase()}
          </AvatarFallback>
        </Avatar>
        <h2 className="text-2xl font-semibold mb-2">{caller.username}</h2>
        <p className="text-lg opacity-80">
          {callStatus === 'incoming' && isIncoming && 'Incoming call...'}
          {callStatus === 'incoming' && !isIncoming && 'Calling...'}
          {callStatus === 'connecting' && 'Connecting...'}
          {callStatus === 'connected' && formatDuration(duration)}
        </p>
      </div>

      {/* Call Controls */}
      <div className="flex items-center justify-center space-x-6">
        {/* Incoming Call Controls */}
        {callStatus === 'incoming' && isIncoming && (
          <>
            <Button
              onClick={onDecline}
              className="w-16 h-16 rounded-full bg-red-500 hover:bg-red-600 text-white border-0"
            >
              <PhoneOff className="w-8 h-8" />
            </Button>
            <Button
              onClick={onAccept}
              className="w-16 h-16 rounded-full bg-green-500 hover:bg-green-600 text-white border-0"
            >
              <Phone className="w-8 h-8" />
            </Button>
          </>
        )}

        {/* Active Call Controls */}
        {(callStatus === 'connecting' || callStatus === 'connected') && (
          <>
            <Button
              onClick={handleMute}
              className={`w-14 h-14 rounded-full border-0 ${
                isMuted 
                  ? 'bg-red-500 hover:bg-red-600' 
                  : 'bg-white/20 hover:bg-white/30'
              }`}
            >
              {isMuted ? <MicOff className="w-6 h-6" /> : <Mic className="w-6 h-6" />}
            </Button>

            <Button
              onClick={handleSpeaker}
              className={`w-14 h-14 rounded-full border-0 ${
                isSpeakerOn 
                  ? 'bg-blue-500 hover:bg-blue-600' 
                  : 'bg-white/20 hover:bg-white/30'
              }`}
            >
              {isSpeakerOn ? <Volume2 className="w-6 h-6" /> : <VolumeX className="w-6 h-6" />}
            </Button>

            <Button
              onClick={onEndCall}
              className="w-16 h-16 rounded-full bg-red-500 hover:bg-red-600 text-white border-0"
            >
              <PhoneOff className="w-8 h-8" />
            </Button>
          </>
        )}

        {/* Outgoing Call Controls */}
        {callStatus === 'incoming' && !isIncoming && (
          <Button
            onClick={onEndCall}
            className="w-16 h-16 rounded-full bg-red-500 hover:bg-red-600 text-white border-0"
          >
            <PhoneOff className="w-8 h-8" />
          </Button>
        )}
      </div>

      {/* Connection Animation */}
      {callStatus === 'connecting' && (
        <div className="mt-8 flex items-center space-x-2">
          <div className="w-2 h-2 bg-white rounded-full animate-bounce"></div>
          <div className="w-2 h-2 bg-white rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
          <div className="w-2 h-2 bg-white rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
        </div>
      )}
    </div>
  );
}

client/src/components/layout/header.tsx.content:
import { Link, useLocation } from "wouter";
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Search, MessageCircle, Heart, Menu, User, Settings, HelpCircle, Info, LogOut, ShieldCheck, Phone, Mail, CheckCircle, DollarSign, AtSign, Megaphone } from "lucide-react";
import { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription, DialogTrigger } from "@/components/ui/dialog";
import { toast } from "@/hooks/use-toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

export function Header() {
  const { user, logout } = useAuth();
  const [location, setLocation] = useLocation();
  const [showAccountCenter, setShowAccountCenter] = useState(false);
  const [showServiceRequest, setShowServiceRequest] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);
  const [followedUsers, setFollowedUsers] = useState<Set<string>>(new Set());
  const queryClient = useQueryClient();

  // Fetch notifications count
  const { data: notificationCount = { count: 0 } } = useQuery({
    queryKey: ['/api/notifications/count'],
    enabled: !!user,
    refetchInterval: 30000, // Refetch every 30 seconds
  });

  // Fetch unread messages count
  const { data: unreadMessagesCount = { totalUnreadCount: 0 } } = useQuery<{ totalUnreadCount: number; messagesTabCount: number; requestsTabCount: number }>({
    queryKey: ['/api/dm/unread-count'],
    enabled: !!user,
    refetchInterval: 30000, // Refetch every 30 seconds
  });

  // Fetch notifications list
  const { data: notifications = [] } = useQuery({
    queryKey: ['/api/notifications'],
    enabled: !!user && showNotifications,
  });

  // Mark notification as read mutation
  const markAsReadMutation = useMutation({
    mutationFn: (notificationId: number) => 
      apiRequest(`/api/notifications/${notificationId}/read`, 'POST'),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/notifications'] });
      queryClient.invalidateQueries({ queryKey: ['/api/notifications/count'] });
    },
  });

  // Mark all as read mutation
  const markAllAsReadMutation = useMutation({
    mutationFn: () => 
      apiRequest('/api/notifications/read-all', 'POST'),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/notifications'] });
      queryClient.invalidateQueries({ queryKey: ['/api/notifications/count'] });
    },
  });



  const handleSearchClick = () => {
    setLocation("/search");
  };

  // Hide header on certain pages
  const hideHeader = ["/trends", "/create", "/circle"].includes(location);
  if (hideHeader) {
    return null;
  }

  // Show menu icon only on profile page
  const isProfilePage = location.startsWith("/profile");

  return (
    <header className="bg-white border-b border-gray-200 sticky top-0 z-50">
      <div className="px-4 py-3 flex items-center justify-between">
        {/* Logo or Username */}
        {isProfilePage ? (
          <h2 className="text-lg font-semibold text-gray-800">
            {user?.username}
          </h2>
        ) : (
          <Link href="/">
            <h1 className="text-xl md:text-2xl font-bold gradient-text cursor-pointer">
              TrendoTalk
            </h1>
          </Link>
        )}

        {/* Right Icons */}
        <div className="flex items-center space-x-3">
          {!isProfilePage && (
            <>
              <Button
                variant="ghost"
                size="sm"
                className="p-2 hover:bg-gray-100 rounded-full"
                onClick={handleSearchClick}
              >
                <Search className="h-5 w-5 text-gray-600" />
              </Button>

              <Button
                variant="ghost"
                size="sm"
                className="p-2 hover:bg-gray-100 rounded-full relative"
                onClick={() => setLocation("/messages")}
              >
                <Mail className="h-5 w-5 text-gray-600" />
                {unreadMessagesCount.totalUnreadCount > 0 && (
                  <Badge variant="destructive" className="absolute -top-1 -right-1 h-5 w-5 p-0 text-xs flex items-center justify-center bg-gradient-to-r from-pink-500 to-purple-600">
                    {unreadMessagesCount.totalUnreadCount > 99 ? '99+' : unreadMessagesCount.totalUnreadCount}
                  </Badge>
                )}
              </Button>

              <DropdownMenu open={showNotifications} onOpenChange={async (isOpen) => {
                setShowNotifications(isOpen);
                if (isOpen && (notificationCount as any)?.count > 0) {
                  // Mark all notifications as read when opening
                  try {
                    const sessionId = localStorage.getItem('sessionId');
                    if (sessionId) {
                      await fetch('/api/notifications/read-all', {
                        method: 'POST',
                        headers: {
                          'Content-Type': 'application/json',
                          'Authorization': `Bearer ${sessionId}`
                        },
                        credentials: 'include'
                      });
                      queryClient.invalidateQueries({ queryKey: ['/api/notifications/count'] });
                    }
                  } catch (error) {
                    console.error('Failed to mark notifications as read:', error);
                  }
                }
              }}>
                <DropdownMenuTrigger asChild>
                  <Button
                    variant="ghost"
                    size="sm"
                    className="p-2 hover:bg-gray-100 rounded-full relative"
                  >
                    <Heart className="h-5 w-5 text-gray-600" />
                    {(notificationCount as any)?.count > 0 && (
                      <Badge variant="destructive" className="absolute -top-1 -right-1 h-5 w-5 p-0 text-xs flex items-center justify-center bg-gradient-to-r from-pink-500 to-purple-600">
                        {(notificationCount as any).count}
                      </Badge>
                    )}
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end" className="w-80 max-h-96 overflow-y-auto">
                  <div className="flex items-center justify-between p-3 border-b">
                    <h3 className="font-semibold text-gray-800">Notifications</h3>
                  </div>

                  {(notifications as any[]).length === 0 ? (
                    <div className="p-6 text-center text-gray-500">
                      <Heart className="h-8 w-8 mx-auto mb-2 text-gray-300" />
                      <p className="text-sm">No notifications yet</p>
                    </div>
                  ) : (
                    <div className="max-h-80 overflow-y-auto">
                      {(notifications as any[]).map((notification: any) => (
                        <DropdownMenuItem
                          key={notification.id}
                          className="p-3 cursor-pointer hover:bg-gray-50 border-b border-gray-100 last:border-b-0"
                          onClick={() => {
                            if (!notification.isRead) {
                              markAsReadMutation.mutate(notification.id);
                            }
                          }}
                        >
                          <div className="flex items-start space-x-3 w-full">
                            <div className="flex-shrink-0">
                              {notification.type === 'like' && (
                                <div className="w-8 h-8 bg-gradient-to-r from-pink-500 to-red-500 rounded-full flex items-center justify-center">
                                  <Heart className="h-4 w-4 text-white fill-current" />
                                </div>
                              )}
                              {notification.type === 'comment' && (
                                <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full flex items-center justify-center">
                                  <MessageCircle className="h-4 w-4 text-white" />
                                </div>
                              )}
                              {notification.type === 'follow' && (
                                <div className="w-8 h-8 bg-gradient-to-r from-green-500 to-teal-500 rounded-full flex items-center justify-center">
                                  <User className="h-4 w-4 text-white" />
                                </div>
                              )}
                            </div>
                            <div className="flex-1 min-w-0">
                              <p className={`text-sm ${notification.isRead ? 'text-gray-600' : 'text-gray-900'}`}>
                                {(() => {
                                  const message = notification.message;
                                  const parts = message.split(' ');
                                  const username = parts[0];
                                  const restOfMessage = parts.slice(1).join(' ');

                                  return (
                                    <>
                                      <span 
                                        className="font-bold text-blue-600 hover:text-blue-800 cursor-pointer hover:underline"
                                        onClick={(e) => {
                                          e.stopPropagation();
                                          setLocation(`/profile/${username}`);
                                        }}
                                      >
                                        {username}
                                      </span>
                                      <span className="font-normal"> {restOfMessage}</span>
                                    </>
                                  );
                                })()}
                              </p>
                              <p className="text-xs text-gray-400 mt-1">
                                {new Date(notification.createdAt).toLocaleDateString()}
                              </p>
                            </div>

                            {/* Post preview for like/comment or Follow Back button */}
                            <div className="flex-shrink-0 ml-2">
                              {(notification.type === 'like' || notification.type === 'comment') && notification.postId && (
                                <div 
                                  className="w-12 h-12 bg-gray-100 rounded-lg border overflow-hidden cursor-pointer hover:opacity-80"
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    setLocation(`/`);
                                  }}
                                >
                                  {notification.postImage ? (
                                    <img 
                                      src={notification.postImage} 
                                      alt="Post preview" 
                                      className="w-full h-full object-cover"
                                      onError={(e) => {
                                        e.currentTarget.style.display = 'none';
                                        const fallback = e.currentTarget.nextElementSibling as HTMLElement;
                                        if (fallback) fallback.style.display = 'flex';
                                      }}
                                    />
                                  ) : notification.postVideo ? (
                                    <video 
                                      src={notification.postVideo} 
                                      className="w-full h-full object-cover"
                                      muted
                                      playsInline
                                    />
                                  ) : null}
                                  <div 
                                    className="w-full h-full flex items-center justify-center bg-gradient-to-r from-purple-500 to-pink-500"
                                    style={{ display: (notification.postImage || notification.postVideo) ? 'none' : 'flex' }}
                                  >
                                    <div className="w-4 h-4 bg-white rounded"></div>
                                  </div>
                                </div>
                              )}

                              {notification.type === 'follow' && (() => {
                                const username = notification.message.split(' ')[0];
                                const isFollowed = followedUsers.has(username);

                                return (
                                  <Button
                                    size="sm"
                                    variant="outline"
                                    className={isFollowed 
                                      ? "text-xs px-3 py-1 h-8 bg-white text-gray-700 border border-gray-300 hover:bg-gray-50"
                                      : "text-xs px-3 py-1 h-8 bg-gradient-to-r from-blue-500 to-purple-500 text-white border-none hover:from-blue-600 hover:to-purple-600"
                                    }
                                    onClick={async (e) => {
                                      e.stopPropagation();

                                      if (isFollowed) return; // Prevent multiple clicks

                                      try {
                                        const sessionId = localStorage.getItem('sessionId');
                                        if (sessionId) {
                                          const response = await fetch(`/api/users/${username}/follow-back`, {
                                            method: 'POST',
                                            headers: {
                                              'Content-Type': 'application/json',
                                              'Authorization': `Bearer ${sessionId}`
                                            },
                                            credentials: 'include'
                                          });

                                          if (response.ok) {
                                            // Update local state immediately
                                            setFollowedUsers(prev => new Set([...prev, username]));

                                            toast({
                                              title: "Success",
                                              description: `You are now following ${username}`,
                                            });

                                            // Refresh notifications
                                            queryClient.invalidateQueries({ queryKey: ['/api/notifications'] });
                                            queryClient.invalidateQueries({ queryKey: ['/api/notifications/count'] });
                                          }
                                        }
                                      } catch (error) {
                                        toast({
                                          title: "Error",
                                          description: "Failed to follow back",
                                          variant: "destructive"
                                        });
                                      }
                                    }}
                                  >
                                    {isFollowed ? "Following" : "Follow Back"}
                                  </Button>
                                );
                              })()}
                            </div>

                            {!notification.isRead && (
                              <div className="flex-shrink-0 ml-2">
                                <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                              </div>
                            )}
                          </div>
                        </DropdownMenuItem>
                      ))}
                    </div>
                  )}
                </DropdownMenuContent>
              </DropdownMenu>
            </>
          )}

          {isProfilePage && (
            <>
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button
                    variant="ghost"
