                  </DropdownMenuItem>

                  <DropdownMenuItem onClick={() => {
                    toast({ title: "Help and Support", description: "Contact us at support@trendotalk.com" });
                  }}>
                    <HelpCircle className="mr-2 h-4 w-4" />
