              {/* Account Center Dialog */}
              <Dialog open={showAccountCenter} onOpenChange={setShowAccountCenter}>
                <DialogContent className="sm:max-w-md">
                  <DialogHeader>
                    <DialogTitle>Account Center</DialogTitle>
