import { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Send, MessageCircle, Heart } from "lucide-react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface CommentModalProps {
  isOpen: boolean;
  onClose: () => void;
  postId: number;
  currentUser?: {
    id: number;
    username: string;
    avatar?: string;
  } | null;
}

interface Comment {
  id: number;
  content: string;
  createdAt: string;
  user: {
    username: string;
    avatar?: string;
  };
}

export function CommentModal({ isOpen, onClose, postId, currentUser }: CommentModalProps) {
  const [newComment, setNewComment] = useState("");
  const queryClient = useQueryClient();

  const { data: comments = [], isLoading } = useQuery<Comment[]>({
    queryKey: [`/api/posts/${postId}/comments`],
    enabled: isOpen && !!postId,
  });

  const addCommentMutation = useMutation({
    mutationFn: async (content: string) => {
      const response = await apiRequest("POST", `/api/posts/${postId}/comments`, { content });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/posts/${postId}/comments`] });
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
      setNewComment("");
    }
  });

  const handleSubmitComment = (e: React.FormEvent) => {
    e.preventDefault();
    if (!currentUser) {
      alert('Please login to comment');
      return;
    }
    if (newComment.trim()) {
      addCommentMutation.mutate(newComment.trim());
    }
  };

  const formatTimeAgo = (dateString: string) => {
    const now = new Date();
    const date = new Date(dateString);
    const diffInSeconds = Math.floor((now.getTime() - date.getTime()) / 1000);

    if (diffInSeconds < 60) return `${diffInSeconds}s ago`;
    if (diffInSeconds < 3600) return `${Math.floor(diffInSeconds / 60)}m ago`;
    if (diffInSeconds < 86400) return `${Math.floor(diffInSeconds / 3600)}h ago`;
    return `${Math.floor(diffInSeconds / 86400)}d ago`;
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[80vh] flex flex-col">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <MessageCircle className="w-5 h-5" />
            <span>Comments</span>
            <span className="text-sm text-gray-500">({comments.length})</span>
          </DialogTitle>
        </DialogHeader>

        <div className="flex-1 flex flex-col space-y-4">
          {/* Comment Input */}
          <form onSubmit={handleSubmitComment} className="flex space-x-3">
            <Avatar className="w-8 h-8">
              <AvatarImage src={currentUser?.avatar} />
              <AvatarFallback>
                {currentUser?.username?.charAt(0).toUpperCase() || "?"}
              </AvatarFallback>
            </Avatar>
            <div className="flex-1 flex space-x-2">
              <Textarea
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
                placeholder={currentUser ? "Add a comment..." : "Please login to comment"}
                disabled={!currentUser || addCommentMutation.isPending}
                className="resize-none min-h-[40px] max-h-[120px]"
                onKeyDown={(e) => {
                  if (e.key === 'Enter' && !e.shiftKey) {
                    e.preventDefault();
                    handleSubmitComment(e);
                  }
                }}
              />
              <Button 
                type="submit" 
                size="sm"
                disabled={!newComment.trim() || !currentUser || addCommentMutation.isPending}
                className="self-end"
              >
                <Send className="w-4 h-4" />
              </Button>
            </div>
          </form>

          {/* Comments List */}
          <ScrollArea className="flex-1 pr-4">
            {isLoading ? (
              <div className="space-y-4">
                {[1, 2, 3].map((i) => (
                  <div key={i} className="flex space-x-3 animate-pulse">
                    <div className="w-8 h-8 bg-gray-200 rounded-full"></div>
                    <div className="flex-1 space-y-2">
                      <div className="w-20 h-3 bg-gray-200 rounded"></div>
                      <div className="w-full h-4 bg-gray-200 rounded"></div>
                    </div>
                  </div>
                ))}
              </div>
            ) : comments.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                <MessageCircle className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                <p>No comments yet.</p>
                <p className="text-sm">Be the first to comment!</p>
              </div>
            ) : (
              <div className="space-y-4">
                {comments.map((comment: Comment) => (
                  <div key={comment.id} className="flex space-x-3">
                    <Avatar className="w-8 h-8">
                      <AvatarImage src={comment.user.avatar} />
                      <AvatarFallback>
                        {comment.user.username.charAt(0).toUpperCase()}
                      </AvatarFallback>
                    </Avatar>
                    <div className="flex-1">
                      <div className="bg-gray-50 rounded-lg p-3">
                        <div className="flex items-center space-x-2 mb-1">
                          <span className="font-medium text-sm">@{comment.user.username}</span>
                          <span className="text-xs text-gray-500">
                            {formatTimeAgo(comment.createdAt)}
                          </span>
                        </div>
                        <p className="text-sm text-gray-800">{comment.content}</p>
                      </div>
                      <div className="flex items-center space-x-4 mt-2 ml-3">
                        <Button variant="ghost" size="sm" className="text-xs text-gray-500 hover:text-red-500 p-0 h-auto">
                          <Heart className="w-3 h-3 mr-1" />
                          Like
                        </Button>
                        <Button variant="ghost" size="sm" className="text-xs text-gray-500 p-0 h-auto">
                          Reply
                        </Button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </ScrollArea>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/post/create-post.t.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { insertPostSchema } from "@shared/schema";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { FileUpload } from "@/components/ui/file-upload";
import { Card, CardContent } from "@/components/ui/card";
import { Switch } from "@/components/ui/switch";
import { z } from "zod";

export function CreatePost() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [, setLocation] = useLocation();
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [previewUrl, setPreviewUrl] = useState<string | null>(null);

  const form = useForm({
    resolver: zodResolver(insertPostSchema.extend({
      isAdminPost: z.boolean().optional(),
    })),
    defaultValues: {
      caption: "",
      link: "",
      isAdminPost: false,
    },
  });

  const createPostMutation = useMutation({
    mutationFn: async (data: any) => {
      const formData = new FormData();
      formData.append("caption", data.caption);
      if (data.link) {
        formData.append("link", data.link);
      }
      if (user?.isAdmin) {
        formData.append("isAdminPost", data.isAdminPost.toString());
      }
      if (selectedFile) {
        formData.append("media", selectedFile);
      }

      const response = await fetch("/api/posts", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("sessionId")}`,
        },
        body: formData,
      });

      if (!response.ok) {
        const error = await response.text();
        throw new Error(error || "Failed to create post");
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
      toast({
        title: "Post created!",
        description: "Your post has been shared successfully.",
      });
      setLocation("/");
    },
    onError: (error: any) => {
      toast({
        title: "Failed to create post",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleFileSelect = (file: File) => {
    setSelectedFile(file);
    const url = URL.createObjectURL(file);
    setPreviewUrl(url);
  };

  const handleFileRemove = () => {
    setSelectedFile(null);
    if (previewUrl) {
      URL.revokeObjectURL(previewUrl);
      setPreviewUrl(null);
    }
  };

  const onSubmit = (data: any) => {
    createPostMutation.mutate(data);
  };

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
        {/* Media Upload */}
        <div className="space-y-4">
          <label className="text-sm font-medium">Media (Optional)</label>
          <FileUpload
            onFileSelect={handleFileSelect}
            onFileRemove={handleFileRemove}
            accept="image/*,video/*"
          />

          {previewUrl && (
            <Card>
              <CardContent className="p-4">
                {selectedFile?.type.startsWith("image/") ? (
                  <img
                    src={previewUrl}
                    alt="Preview"
                    className="w-full h-auto max-h-64 object-cover rounded"
                  />
                ) : (
                  <video
                    src={previewUrl}
                    className="w-full h-auto max-h-64 object-cover rounded"
                    controls
                  />
                )}
              </CardContent>
            </Card>
          )}
        </div>

        {/* Caption */}
        <FormField
          control={form.control}
          name="caption"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Caption</FormLabel>
              <FormControl>
                <Textarea
                  placeholder="What's on your mind?"
                  className="min-h-[100px] resize-none"
                  {...field}
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Link */}
        <FormField
          control={form.control}
          name="link"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Link (Optional)</FormLabel>
              <FormControl>
                <Input 
                  placeholder="https://example.com" 
                  {...field} 
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Admin Options */}
        {user?.isAdmin && (
          <FormField
            control={form.control}
            name="isAdminPost"
            render={({ field }) => (
              <FormItem className="flex flex-row items-center justify-between rounded-lg border p-4">
                <div className="space-y-0.5">
                  <FormLabel className="text-base">Admin Post</FormLabel>
                  <div className="text-sm text-gray-500">
                    Post to homepage feed (visible to all users)
                  </div>
                </div>
                <FormControl>
                  <Switch
                    checked={field.value}
                    onCheckedChange={field.onChange}
                  />
                </FormControl>
              </FormItem>
            )}
          />
        )}

        {/* Submit Button */}
        <Button
          type="submit"
          className="w-full gradient-bg text-white hover:opacity-90"
          disabled={createPostMutation.isPending}
        >
          {createPostMutation.isPending ? "Creating..." : user?.isAdmin ? "Create Post" : "Create Trend"}
        </Button>
      </form>
    </Form>
  );
}

client/src/components/post/create-regula.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Upload, X, ExternalLink } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";

export function CreateRegularPost() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [caption, setCaption] = useState("");
  const [link, setLink] = useState("");
  const [media, setMedia] = useState<File | null>(null);
  const [mediaPreview, setMediaPreview] = useState<string>("");

  const createPostMutation = useMutation({
    mutationFn: async (postData: any) => {
      const formData = new FormData();
      formData.append('caption', postData.caption);
      if (postData.link) formData.append('link', postData.link);
      if (media) formData.append('media', media);

      const response = await fetch('/api/posts', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('sessionId')}`
        },
        body: formData,
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to create post');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Post created successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
      resetForm();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create post",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setCaption("");
    setLink("");
    setMedia(null);
    setMediaPreview("");
  };

  const handleMediaUpload = (file: File) => {
    setMedia(file);
    setMediaPreview(URL.createObjectURL(file));
  };

  const removeMedia = () => {
    if (mediaPreview) {
      URL.revokeObjectURL(mediaPreview);
    }
    setMedia(null);
    setMediaPreview("");
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!caption.trim()) {
      toast({
        title: "Error",
        description: "Caption is required",
        variant: "destructive",
      });
      return;
    }

    createPostMutation.mutate({
      caption: caption.trim(),
      link: link.trim() || undefined,
    });
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      {/* Media Upload */}
      <div className="space-y-4">
        <Label className="text-base font-semibold">Media (Optional)</Label>
        <div className="relative aspect-video border-2 border-dashed border-gray-300 rounded-lg overflow-hidden max-w-md">
          {mediaPreview ? (
            <div className="relative w-full h-full">
              {media?.type.startsWith('video/') ? (
                <video
                  src={mediaPreview}
                  className="w-full h-full object-cover"
                  controls
                />
              ) : (
                <img
                  src={mediaPreview}
                  alt="Preview"
                  className="w-full h-full object-cover"
                />
              )}
              <Button
                type="button"
                variant="destructive"
                size="sm"
                className="absolute top-2 right-2"
                onClick={removeMedia}
              >
                <X className="w-4 h-4" />
              </Button>
            </div>
          ) : (
            <label className="flex flex-col items-center justify-center w-full h-full cursor-pointer hover:bg-gray-50">
              <Upload className="w-8 h-8 text-gray-400 mb-2" />
              <span className="text-sm text-gray-500">Upload Image or Video</span>
              <input
                type="file"
                accept="image/*,video/*"
                className="hidden"
                onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) handleMediaUpload(file);
                }}
              />
            </label>
          )}
        </div>
      </div>

      {/* Caption */}
      <div className="space-y-2">
        <Label htmlFor="caption">Caption *</Label>
        <Textarea
          id="caption"
          value={caption}
          onChange={(e) => setCaption(e.target.value)}
          placeholder="What's on your mind..."
          className="min-h-[100px]"
          required
        />
      </div>

      {/* Link */}
      <div className="space-y-2">
        <Label htmlFor="link">Link (Optional)</Label>
        <div className="flex">
          <Input
            id="link"
            value={link}
            onChange={(e) => setLink(e.target.value)}
            placeholder="https://example.com"
            className="flex-1"
          />
          <Button
            type="button"
            variant="outline"
            size="sm"
            className="ml-2"
            disabled={!link.trim()}
            onClick={() => link.trim() && window.open(link, '_blank')}
          >
            <ExternalLink className="w-4 h-4" />
          </Button>
        </div>
      </div>

      {/* Submit Button */}
      <Button
        type="submit"
        className="w-full gradient-bg text-white"
        disabled={createPostMutation.isPending}
      >
        {createPostMutation.isPending ? "Creating..." : "Create Post"}
      </Button>
    </form>
  );
}

client/src/components/post/create-video-.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Upload, X, ExternalLink } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";

interface VideoUpload {
  file: File | null;
  url: string;
}

export function CreateVideoPost() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [title, setTitle] = useState("");
  const [rank, setRank] = useState("");
  const [otherRankPlatform, setOtherRankPlatform] = useState("");
  const [otherRankNumber, setOtherRankNumber] = useState("");
  const [type, setType] = useState("");
  const [detailsLink, setDetailsLink] = useState("");
  const [videos, setVideos] = useState<VideoUpload[]>([
    { file: null, url: "" },
    { file: null, url: "" },
    { file: null, url: "" }
  ]);

  const createPostMutation = useMutation({
    mutationFn: async (postData: any) => {
      const formData = new FormData();
      formData.append('title', postData.title);
      formData.append('rank', postData.rank);
      if (postData.otherRank) formData.append('otherRank', postData.otherRank);
      if (postData.type) formData.append('type', postData.type);
      if (postData.detailsLink) formData.append('detailsLink', postData.detailsLink);

      // Add video files
      videos.forEach((video, index) => {
        if (video.file) {
          formData.append(`video${index + 1}`, video.file);
        }
      });

      const response = await fetch('/api/admin/posts', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('sessionId')}`
        },
        body: formData,
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to create post');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Video post created successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
      resetForm();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create post",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setTitle("");
    setRank("");
    setOtherRankPlatform("");
    setOtherRankNumber("");
    setType("");
    setDetailsLink("");
    setVideos([
      { file: null, url: "" },
      { file: null, url: "" },
      { file: null, url: "" }
    ]);
  };

  const handleVideoUpload = (index: number, file: File) => {
    const newVideos = [...videos];
    newVideos[index] = { file, url: URL.createObjectURL(file) };
    setVideos(newVideos);
  };

  const removeVideo = (index: number) => {
    const newVideos = [...videos];
    if (newVideos[index].url) {
      URL.revokeObjectURL(newVideos[index].url);
    }
    newVideos[index] = { file: null, url: "" };
    setVideos(newVideos);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Only require at least one video - all other fields are optional for admin
    const hasVideos = videos.some(video => video.file);
    if (!hasVideos) {
      toast({
        title: "Error",
        description: "At least one video is required",
        variant: "destructive",
      });
      return;
    }

    // Build other rank string if platform and number are provided
    const otherRank = otherRankPlatform && otherRankNumber 
      ? `on ${otherRankPlatform}:#${otherRankNumber}`
      : undefined;

    createPostMutation.mutate({
      title: title.trim() || undefined,
      rank: rank ? Number(rank) : undefined,
      otherRank,
      type: type.trim() || undefined,
      detailsLink: detailsLink.trim() || undefined,
    });
  };

  if (!user?.isAdmin) {
    return (
      <Card>
        <CardContent className="p-6 text-center">
          <p className="text-gray-500">Admin access required to create video posts.</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      {/* Video Uploads */}
      <div className="space-y-4">
        <Label className="text-base font-semibold">Videos (Max 3)</Label>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {videos.map((video, index) => (
            <div key={index} className="space-y-2">
              <Label className="text-sm text-gray-600">Video {index + 1}</Label>
              <div className="relative aspect-video border-2 border-dashed border-gray-300 rounded-lg overflow-hidden">
                {video.url ? (
                  <div className="relative w-full h-full">
                    <video
                      src={video.url}
                      className="w-full h-full object-cover"
                      controls
                    />
                    <Button
                      type="button"
                      variant="destructive"
                      size="sm"
                      className="absolute top-2 right-2"
                      onClick={() => removeVideo(index)}
                    >
                      <X className="w-4 h-4" />
                    </Button>
                  </div>
                ) : (
                  <label className="flex flex-col items-center justify-center w-full h-full cursor-pointer hover:bg-gray-50">
                    <Upload className="w-8 h-8 text-gray-400 mb-2" />
                    <span className="text-sm text-gray-500">Upload Video</span>
                    <input
                      type="file"
                      accept="video/*"
                      className="hidden"
                      onChange={(e) => {
                        const file = e.target.files?.[0];
                        if (file) handleVideoUpload(index, file);
                      }}
                    />
                  </label>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Title */}
      <div className="space-y-2">
        <Label htmlFor="title">Title</Label>
        <Textarea
          id="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Enter video title..."
          className="min-h-[80px]"
        />
      </div>

      {/* Rank and Other Rank */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="space-y-2">
          <Label htmlFor="rank">Rank</Label>
          <Input
            id="rank"
            type="number"
            value={rank}
            onChange={(e) => setRank(e.target.value)}
            placeholder="1"
            min="1"
          />
        </div>
        <div className="space-y-2">
          <Label>Other Rank (Optional)</Label>
          <div className="flex space-x-2">
            <Select value={otherRankPlatform} onValueChange={setOtherRankPlatform}>
              <SelectTrigger className="flex-1">
                <SelectValue placeholder="Platform" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="youtube">YouTube</SelectItem>
                <SelectItem value="instagram">Instagram</SelectItem>
                <SelectItem value="ipl">IPL</SelectItem>
                <SelectItem value="x">X</SelectItem>
                <SelectItem value="films">Films</SelectItem>
                <SelectItem value="songs">Songs</SelectItem>
                <SelectItem value="model">Model</SelectItem>
              </SelectContent>
            </Select>
            <Input
              type="number"
              value={otherRankNumber}
              onChange={(e) => setOtherRankNumber(e.target.value)}
              placeholder="Rank"
              className="w-20"
              min="1"
            />
          </div>
          <p className="text-xs text-gray-500">
            Example: Select "Instagram" + "6" = "on instagram:#6"
          </p>
        </div>
      </div>

      {/* Type */}
      <div className="space-y-2">
        <Label htmlFor="type">Type (Custom trend type)</Label>
        <Input
          id="type"
          value={type}
          onChange={(e) => setType(e.target.value)}
          placeholder="e.g., Viral Dance, Comedy Skit, Tutorial..."
        />
        <p className="text-xs text-gray-500">Describe what type of trend this post represents</p>
      </div>

      {/* Details Link */}
      <div className="space-y-2">
        <Label htmlFor="detailsLink">Details Link</Label>
        <div className="flex">
          <Input
            id="detailsLink"
            value={detailsLink}
            onChange={(e) => setDetailsLink(e.target.value)}
            placeholder="https://example.com"
            className="flex-1"
          />
          <Button
            type="button"
            variant="outline"
            size="sm"
            className="ml-2"
            disabled={!detailsLink.trim()}
            onClick={() => detailsLink.trim() && window.open(detailsLink, '_blank')}
          >
            <ExternalLink className="w-4 h-4" />
          </Button>
        </div>
      </div>

      {/* Submit Button */}
      <Button
        type="submit"
        className="w-full gradient-bg text-white"
        disabled={createPostMutation.isPending}
      >
        {createPostMutation.isPending ? "Creating..." : "Create Video Post"}
      </Button>
    </form>
  );
}

client/src/components/post/edit-post-mod.content:
import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface EditPostModalProps {
  isOpen: boolean;
  onClose: () => void;
  post: {
    id: number;
    title?: string;
    rank?: number;
    otherRank?: string;
    category?: string;
    type?: string;
    detailsLink?: string;
  };
}

export function EditPostModal({ isOpen, onClose, post }: EditPostModalProps) {
  const [formData, setFormData] = useState({
    title: post.title || "",
    rank: post.rank?.toString() || "",
    otherRank: post.otherRank || "",
    category: post.category || "",
    type: post.type || "",
    detailsLink: post.detailsLink || "",
  });

  const queryClient = useQueryClient();

  const updatePostMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      const response = await apiRequest("PUT", `/api/posts/${post.id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
      onClose();
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    updatePostMutation.mutate(formData);
  };

  const handleChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle>Edit Post</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="title">Title</Label>
            <Textarea
              id="title"
              value={formData.title}
              onChange={(e) => handleChange("title", e.target.value)}
              placeholder="Post title"
              className="min-h-[80px]"
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="rank">Rank</Label>
              <Input
                id="rank"
                type="number"
                value={formData.rank}
                onChange={(e) => handleChange("rank", e.target.value)}
                placeholder="1"
              />
            </div>
            <div>
              <Label htmlFor="otherRank">Other Rank</Label>
              <Input
                id="otherRank"
                value={formData.otherRank}
                onChange={(e) => handleChange("otherRank", e.target.value)}
                placeholder="#1 on insta"
              />
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="category">Category</Label>
              <Input
                id="category"
                value={formData.category}
                onChange={(e) => handleChange("category", e.target.value)}
                placeholder="youtube"
              />
            </div>
            <div>
              <Label htmlFor="type">Type</Label>
              <Input
                id="type"
                value={formData.type}
                onChange={(e) => handleChange("type", e.target.value)}
                placeholder="reels"
              />
            </div>
          </div>

          <div>
            <Label htmlFor="detailsLink">Full Details Link</Label>
            <Input
              id="detailsLink"
              type="url"
              value={formData.detailsLink}
              onChange={(e) => handleChange("detailsLink", e.target.value)}
              placeholder="https://example.com"
            />
          </div>

          <div className="flex justify-end space-x-2">
            <Button type="button" variant="outline" onClick={onClose}>
              Cancel
            </Button>
            <Button type="submit" disabled={updatePostMutation.isPending}>
              {updatePostMutation.isPending ? "Saving..." : "Save Changes"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/post/post-card.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { formatDistanceToNow } from "date-fns";
import { useAuth } from "@/hooks/use-auth";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent } from "@/components/ui/card";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Heart, MessageCircle, Share, Bookmark, MoreHorizontal, Play } from "lucide-react";

interface PostCardProps {
  post: {
    id: number;
    caption: string;
    imageUrl?: string;
    videoUrl?: string;
    likesCount: number;
    commentsCount: number;
    isLiked?: boolean;
    createdAt: string;
    user: {
      id: number;
      username: string;
      avatar?: string;
      isAdmin: boolean;
    };
  };
}

export function PostCard({ post }: PostCardProps) {
  const { isAuthenticated } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [commentText, setCommentText] = useState("");
  const [showComments, setShowComments] = useState(false);

  const likeMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", `/api/posts/${post.id}/like`);
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
      toast({
        title: data.liked ? "Post liked!" : "Post unliked!",
        description: `${data.likesCount} ${data.likesCount === 1 ? 'like' : 'likes'}`,
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update like status",
        variant: "destructive",
      });
    },
  });

  const commentMutation = useMutation({
    mutationFn: async (content: string) => {
      const response = await apiRequest("POST", `/api/posts/${post.id}/comments`, { content });
      return response.json();
    },
    onSuccess: () => {
      setCommentText("");
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
      toast({
        title: "Comment added!",
        description: "Your comment has been posted.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to add comment",
        variant: "destructive",
      });
    },
  });

  const handleLike = () => {
    if (!isAuthenticated) {
      toast({
        title: "Login required",
        description: "Please login to like posts",
        variant: "destructive",
      });
      return;
    }
    likeMutation.mutate();
  };

  const handleComment = () => {
    if (!commentText.trim()) return;
    if (!isAuthenticated) {
      toast({
        title: "Login required",
        description: "Please login to comment",
        variant: "destructive",
      });
      return;
    }
    commentMutation.mutate(commentText.trim());
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleComment();
    }
  };

  return (
    <Card className="overflow-hidden">
      <CardContent className="p-0">
        {/* Post Header */}
        <div className="flex items-center justify-between p-4">
          <div className="flex items-center space-x-3">
            <Avatar className="w-10 h-10">
              <AvatarImage src={post.user.avatar} alt={post.user.username} />
              <AvatarFallback>{post.user.username[3]?.toUpperCase()}</AvatarFallback>
            </Avatar>
            <div>
              <h4 className="font-semibold text-gray-800 flex items-center gap-2">
                {post.user.username}
                {post.user.isAdmin && (
                  <Badge variant="secondary" className="gradient-bg text-white text-xs">
                    <i className="fas fa-check-circle mr-1"></i>
                  </Badge>
                )}
              </h4>
              <p className="text-xs text-gray-500">
                {formatDistanceToNow(new Date(post.createdAt), { addSuffix: true })}
              </p>
            </div>
          </div>
          <Button variant="ghost" size="sm" className="text-gray-400 hover:text-gray-600">
            <MoreHorizontal className="h-4 w-4" />
          </Button>
        </div>

        {/* Post Media */}
        {(post.imageUrl || post.videoUrl) && (
          <div className="relative">
            {post.imageUrl ? (
              <img 
                src={post.imageUrl} 
                alt="Post content" 
                className="w-full h-auto object-cover max-h-96"
              />
            ) : post.videoUrl ? (
              <div className="relative bg-black">
                <video 
                  src={post.videoUrl}
                  className="w-full h-auto object-cover max-h-96"
                  controls={false}
                  poster={post.imageUrl}
                />
                <div className="absolute inset-0 flex items-center justify-center">
                  <Button className="bg-white bg-opacity-90 rounded-full p-4 hover:bg-opacity-100">
                    <Play className="h-6 w-6 text-blue-600 ml-1" />
                  </Button>
                </div>
              </div>
            ) : null}
          </div>
        )}

        {/* Post Actions */}
        <div className="p-4">
          <div className="flex items-center justify-between mb-3">
            <div className="flex items-center space-x-4">
              <Button
                variant="ghost"
                size="sm"
                className={`p-0 hover:text-red-500 transition-colors ${
                  post.isLiked ? "text-red-500" : "text-gray-600"
                }`}
                onClick={handleLike}
                disabled={likeMutation.isPending}
              >
                <Heart className={`h-6 w-6 ${post.isLiked ? "fill-current" : ""}`} />
              </Button>
              <Button
                variant="ghost"
                size="sm"
                className="p-0 hover:text-gray-700 transition-colors text-gray-600"
                onClick={() => setShowComments(!showComments)}
              >
                <MessageCircle className="h-6 w-6" />
              </Button>
              <Button
                variant="ghost"
                size="sm"
                className="p-0 hover:text-gray-700 transition-colors text-gray-600"
              >
                <Share className="h-6 w-6" />
              </Button>
            </div>
            <Button
              variant="ghost"
              size="sm"
              className="p-0 hover:text-gray-700 transition-colors text-gray-600"
            >
              <Bookmark className="h-6 w-6" />
            </Button>
          </div>

          <p className="font-semibold text-gray-800 mb-2">
            {post.likesCount} {post.likesCount === 1 ? 'like' : 'likes'}
          </p>

          <div className="mb-2">
            <span className="font-semibold text-gray-800">{post.user.username}</span>
            <span className="text-gray-700 ml-2">{post.caption}</span>
          </div>

          {post.commentsCount > 0 && (
            <Button
              variant="ghost"
              size="sm"
              className="p-0 text-gray-500 text-sm mb-2 hover:text-gray-700"
              onClick={() => setShowComments(!showComments)}
            >
              View all {post.commentsCount} comments
            </Button>
          )}

          {/* Comment Input */}
          {isAuthenticated && (
            <div className="flex items-center space-x-3 pt-2 border-t border-gray-100">
              <Avatar className="w-8 h-8">
                <AvatarImage src="https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?ixlib=rb-4.0.3&auto=format&fit=crop&w=100&h=100" />
                <AvatarFallback>U</AvatarFallback>
              </Avatar>
              <Input
                type="text"
                placeholder="Add a comment..."
                value={commentText}
                onChange={(e) => setCommentText(e.target.value)}
                onKeyPress={handleKeyPress}
                className="flex-1 border-none bg-transparent outline-none focus:ring-0 text-gray-700 placeholder-gray-400"
              />
              {commentText.trim() && (
                <Button
                  size="sm"
                  className="text-blue-500 font-medium hover:text-purple-600"
                  onClick={handleComment}
                  disabled={commentMutation.isPending}
                >
                  {commentMutation.isPending ? "Posting..." : "Post"}
                </Button>
              )}
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/post/unified-post-.content:
import { useState, useRef, useEffect } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { ThumbsUp, ThumbsDown, MessageCircle, ExternalLink, ChevronDown, ChevronUp, Play, Pause, VolumeX, Volume2, Share2, Edit, Trash2 } from "lucide-react";
import { CachedImage } from "@/components/ui/cached-image";
import { CachedVideo } from "@/components/ui/cached-video";
import { cn } from "@/lib/utils";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { CommentModal } from "./comment-modal";
import { EditPostModal } from "./edit-post-modal";

interface UnifiedPostCardProps {
  post: {
    id: number;
    title?: string;
    caption?: string;
    video1Url?: string;
    video2Url?: string;
    video3Url?: string;
    imageUrl?: string;
    videoUrl?: string;
    rank?: number;
    otherRank?: string;
    category?: string;
    type?: string;
    detailsLink?: string;
    link?: string;
    likesCount?: number;
    dislikesCount?: number;
    votesCount?: number;
    isAdminPost?: boolean;
    user: {
      username: string;
      avatar?: string;
      isAdmin?: boolean;
    };
  };
  currentUser?: {
    id: number;
    username: string;
    isAdmin: boolean;
  } | null;
  onVideoRefsReady?: (postId: number, videos: HTMLVideoElement[]) => void;
}

export function UnifiedPostCard({ post, currentUser, onVideoRefsReady }: UnifiedPostCardProps) {
  const [expandedContent, setExpandedContent] = useState(false);
  const [activeVideo, setActiveVideo] = useState<number | null>(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(true);
  const [commentModalOpen, setCommentModalOpen] = useState(false);
  const [editModalOpen, setEditModalOpen] = useState(false);

  // Track interaction states
  const [isLiked, setIsLiked] = useState(false);
  const [isDisliked, setIsDisliked] = useState(false);
  const [isVoted, setIsVoted] = useState(false);

  const videoRefs = useRef<(HTMLVideoElement | null)[]>([]);
  const singleVideoRef = useRef<HTMLVideoElement | null>(null);

  // Collect video elements for autoplay system
  const collectVideoRefs = () => {
    const videos: HTMLVideoElement[] = [];
    if (isAdminVideoPost) {
      videoRefs.current.forEach(video => {
        if (video) videos.push(video);
      });
    } else if (singleVideoRef.current) {
      videos.push(singleVideoRef.current);
    }
    if (videos.length > 0 && onVideoRefsReady) {
      onVideoRefsReady(post.id, videos);
    }
  };

  // Determine if this is an admin video post or regular post
  const isAdminVideoPost = post.isAdminPost && (post.video1Url || post.video2Url || post.video3Url);
  const isRegularPost = !post.isAdminPost;

  // Initialize interaction states from post data
  useEffect(() => {
    setIsLiked((post as any).isLiked || false);
    setIsDisliked((post as any).isDisliked || false);
    setIsVoted((post as any).isVoted || false);
  }, [post]);

  // Auto-start videos when component mounts
  useEffect(() => {
    const startAutoplay = () => {
      if (isAdminVideoPost && videoRefs.current[0]) {
        // Auto-start first admin video muted
        const firstVideo = videoRefs.current[0];
        firstVideo.muted = true;
        firstVideo.play().catch(() => {
          // Autoplay failed, which is normal on some browsers
        });
        setActiveVideo(0);
        setIsPlaying(true);
      } else if (singleVideoRef.current) {
        // Auto-start regular video muted
        const video = singleVideoRef.current;
        video.muted = true;
        video.play().catch(() => {
          // Autoplay failed, which is normal on some browsers
        });
        setIsPlaying(true);
      }
    };

    // Small delay to ensure video elements are ready
    setTimeout(startAutoplay, 200);
  }, [isAdminVideoPost]);

  // For admin video posts
  const adminVideos = [post.video1Url, post.video2Url, post.video3Url].filter(Boolean);
  const videoCount = adminVideos.length;

  const handleAdminVideoClick = (index: number) => {
    const video = videoRefs.current[index];
    if (!video) return;

    // Pause all other videos first
    videoRefs.current.forEach((v, i) => {
      if (v && i !== index) {
        v.pause();
        v.muted = true;
      }
    });

    if (activeVideo === index) {
      // If this video is already active, toggle mute/unmute
      video.muted = !video.muted;
      setIsMuted(video.muted);
    } else {
      // Play this video and unmute it
      video.muted = false;
      setIsMuted(false);
      video.play().catch(console.error);
      setActiveVideo(index);
      setIsPlaying(true);
    }
  };

  const handleRegularVideoClick = () => {
    const video = singleVideoRef.current;
    if (!video) return;

    if (isPlaying) {
      if (isMuted) {
        video.muted = false;
        setIsMuted(false);
      } else {
        video.pause();
        setIsPlaying(false);
      }
    } else {
      video.muted = false;
      setIsMuted(false);
      video.play().catch(console.error);
      setIsPlaying(true);
    }
  };

  const getVideoLayout = () => {
    if (videoCount === 1) return "grid-cols-1";
    if (videoCount === 2) return "grid-cols-2 gap-1";
    if (videoCount === 3) return "grid-cols-3 gap-1";
    return "grid-cols-1";
  };

  const formatContent = (content: string) => {
    if (expandedContent || content.length <= 100) {
      return content;
    }
    return content.substring(0, 100) + "...";
  };

  const formatOtherRank = (otherRank?: string) => {
    if (!otherRank) return null;
    // Convert "on youtube:#1" to "#1 on youtube"
    const match = otherRank.match(/on\s+(\w+):#(\d+)/i);
    if (match) {
      const [, platform, rank] = match;
      return `#${rank} on ${platform}`;
    }
    return otherRank;
  };

  const queryClient = useQueryClient();

  const likeMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", `/api/posts/${post.id}/like`);
      return response.json();
    },
    onSuccess: (data) => {
      // Update local state immediately - like/dislike are mutually exclusive
      setIsLiked(data.liked);
      setIsDisliked(false); // Always remove dislike when liking

      // Update post in cache with new counts
      queryClient.setQueryData(["/api/posts"], (oldPosts: any[]) => {
        if (!oldPosts) return oldPosts;
        return oldPosts.map(p => 
          p.id === post.id 
            ? { 
                ...p, 
                likesCount: data.likesCount, 
                dislikesCount: data.dislikesCount || 0, 
                isLiked: data.liked,
                isDisliked: false 
              }
            : p
        );
      });
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
    }
  });

  const dislikeMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", `/api/posts/${post.id}/dislike`);
      return response.json();
    },
    onSuccess: (data) => {
      // Update local state immediately - like/dislike are mutually exclusive
      console.log('Dislike response:', data);
      setIsDisliked(data.disliked);
      setIsLiked(false); // Always remove like when disliking
      console.log('After dislike update:', { isDisliked: data.disliked, isLiked: false });

      // Update post in cache with new counts
      queryClient.setQueryData(["/api/posts"], (oldPosts: any[]) => {
        if (!oldPosts) return oldPosts;
        return oldPosts.map(p => 
          p.id === post.id 
            ? { 
                ...p, 
                likesCount: data.likesCount || 0, 
                dislikesCount: data.dislikesCount, 
                isLiked: false,
                isDisliked: data.disliked 
              }
            : p
        );
      });
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
    }
  });

  const voteMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", `/api/posts/${post.id}/vote`);
      return response.json();
    },
    onSuccess: (data) => {
      // Update local state immediately
      console.log('Vote response:', data);
      setIsVoted(data.voted);
      console.log('After vote update:', { isVoted: data.voted });

      // Update post in cache with new vote count
      queryClient.setQueryData(["/api/posts"], (oldPosts: any[]) => {
        if (!oldPosts) return oldPosts;
        return oldPosts.map(p => 
          p.id === post.id 
            ? { ...p, votesCount: data.votesCount, isVoted: data.voted }
            : p
        );
      });
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
    }
  });

  const handleLike = () => {
    if (!currentUser) {
      alert('Please login to like posts');
      return;
    }
    likeMutation.mutate();
  };

  const handleDislike = () => {
    if (!currentUser) {
      alert('Please login to dislike posts');
      return;
    }
    console.log('Before dislike:', { isDisliked, isLiked });
    dislikeMutation.mutate();
  };

  const handleVote = () => {
    if (!currentUser) {
      // Redirect to login or show login modal
      alert('Please login to vote');
      return;
    }
    console.log('Before vote:', { isVoted });
    voteMutation.mutate();
  };

  const handleComment = () => {
    setCommentModalOpen(true);
  };

  const deletePostMutation = useMutation({
    mutationFn: async () => {
      // Use admin delete endpoint if user is admin
      const endpoint = currentUser?.isAdmin ? `/api/admin/posts/${post.id}` : `/api/posts/${post.id}`;
      const response = await apiRequest("DELETE", endpoint);
      return response.json();
    },
    onSuccess: () => {
      // Remove post from cache immediately
      queryClient.setQueryData(["/api/posts"], (oldPosts: any[]) => {
        if (!oldPosts) return oldPosts;
        return oldPosts.filter(p => p.id !== post.id);
      });
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
    }
  });

  const handleDelete = () => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      deletePostMutation.mutate();
    }
  };

  const handleEdit = () => {
    setEditModalOpen(true);
  };

  const handleShare = async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: post.title || post.caption || 'TrendoTalk Post',
          text: post.title || post.caption || 'Check out this post on TrendoTalk',
          url: window.location.href
        });
      } catch (error) {
        console.log('Share cancelled');
      }
    } else {
      // Fallback for browsers that don't support Web Share API
      navigator.clipboard.writeText(window.location.href);
      // You could show a toast here indicating the link was copied
    }
  };

  if (isAdminVideoPost) {
    return (
      <>
        <Card className="w-full max-w-2xl mx-auto bg-white shadow-lg rounded-lg overflow-hidden mb-6">
          <CardContent className="p-0">
            {/* Fixed 16:9 Video Container */}
            <div className="relative w-full aspect-video bg-black">
              <div className={cn("grid h-full", getVideoLayout())}>
                {adminVideos.map((videoUrl, index) => (
                  <div key={`video-${post.id}-${index}`} className="relative h-full overflow-hidden">
                    <CachedVideo
                      src={videoUrl || ""}
                      className="w-full h-full object-cover cursor-pointer"
                      controls={false}
                      autoPlay={false}
                      muted={true}
                      loop={true}
                      playsInline
                      onClick={() => handleAdminVideoClick(index)}
                    />
                    {/* Video Controls Overlay */}
                    <div className="absolute inset-0 bg-black bg-opacity-20 opacity-0 hover:opacity-100 transition-opacity flex items-center justify-center pointer-events-none">
                      <div className="text-white pointer-events-auto">
                        {activeVideo === index ? (
                          isMuted ? <VolumeX className="w-8 h-8" /> : <Volume2 className="w-8 h-8" />
                        ) : (
                          <Play className="w-8 h-8" />
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </CardContent>

          {/* Post Details Section - Always Visible */}
          <div className="bg-white p-4 space-y-3 border-t">
            {/* First Line: Rank, Other Rank, Type, Full Details */}
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                {post.rank && (
                  <div className="bg-gradient-to-r from-purple-500 to-pink-500 text-white px-2 py-1 rounded text-xs font-bold">
                    #{post.rank}
                  </div>
                )}
                {formatOtherRank(post.otherRank) && (
                  <div className="bg-gray-100 text-gray-700 px-2 py-1 rounded text-xs">
                    {formatOtherRank(post.otherRank)}
                  </div>
                )}
                {post.type && (
                  <div className="bg-purple-100 text-purple-700 px-2 py-1 rounded text-xs">
                    Trend type - {post.type}
                  </div>
                )}
              </div>
              {post.detailsLink && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => window.open(post.detailsLink, '_blank')}
                  className="hover:bg-gray-50 text-xs text-blue-600"
                >
                  full details
                </Button>
              )}
            </div>

            {/* Second & Third Line: Title */}
            {post.title && (
              <div>
                <p className="text-gray-900 text-sm leading-relaxed font-medium">
                  {formatContent(post.title)}
                </p>
                {post.title.length > 100 && (
                  <Button
                    variant="ghost"
                    size="sm"
                    className="p-0 h-auto text-blue-600 hover:text-blue-800 text-xs mt-1"
                    onClick={() => setExpandedContent(!expandedContent)}
                  >
                    {expandedContent ? "Show less" : "Show more"}
                  </Button>
                )}
              </div>
            )}

            {/* Fourth Line: Likes, Dislikes, Votes, Comments, Share */}
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <Button 
                  variant="ghost" 
                  size="sm" 
                  onClick={handleLike}
                  disabled={likeMutation.isPending}
                  className={cn(
                    "flex items-center space-x-1 text-xs transition-colors",
                    isLiked 
                      ? "bg-green-100 text-green-700 hover:bg-green-150" 
                      : "hover:bg-green-50 hover:text-green-600"
                  )}
                >
                  <ThumbsUp className={cn("w-3 h-3", isLiked && "fill-current")} />
                  <span>{post.likesCount || 0}</span>
                </Button>
                <Button 
                  variant="ghost" 
                  size="sm" 
                  onClick={handleDislike}
                  disabled={dislikeMutation.isPending}
                  className={cn(
                    "flex items-center space-x-1 text-xs transition-colors",
                    isDisliked 
                      ? "bg-red-100 text-red-700 hover:bg-red-150" 
                      : "hover:bg-red-50 hover:text-red-600"
                  )}
                >
                  <ThumbsDown className={cn("w-3 h-3", isDisliked && "fill-current")} />
                  <span>{post.dislikesCount || 0}</span>
                </Button>
                <Button 
                  variant="ghost" 
                  size="sm" 
                  onClick={handleVote}
                  disabled={voteMutation.isPending}
                  className={cn(
                    "flex items-center space-x-1 text-xs transition-colors",
                    isVoted 
                      ? "bg-blue-100 text-blue-700 hover:bg-blue-150" 
                      : "hover:bg-blue-50 hover:text-blue-600"
                  )}
                >
                  <span>vote</span>
                  <span>{post.votesCount || 0}</span>
                </Button>
              </div>
              <div className="flex items-center space-x-2">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={handleComment}
                  className="hover:bg-gray-50"
                >
                  <MessageCircle className="w-3 h-3" />
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={handleShare}
                  className="hover:bg-gray-50"
                >
                  <Share2 className="w-3 h-3" />
                </Button>
                {/* Admin Edit/Delete Buttons */}
                {currentUser?.isAdmin && (
                  <>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={handleEdit}
                      className="hover:bg-blue-50 hover:text-blue-600"
                    >
                      <Edit className="w-3 h-3" />
                    </Button>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={handleDelete}
                      disabled={deletePostMutation.isPending}
                      className="hover:bg-red-50 hover:text-red-600"
                    >
                      <Trash2 className="w-3 h-3" />
                    </Button>
                  </>
                )}
              </div>
            </div>
          </div>
        </Card>

        {/* Comment Modal */}
        <CommentModal 
          isOpen={commentModalOpen}
          onClose={() => setCommentModalOpen(false)}
          postId={post.id}
          currentUser={currentUser}
        />

        {/* Edit Modal */}
        {currentUser?.isAdmin && (
          <EditPostModal 
            isOpen={editModalOpen}
            onClose={() => setEditModalOpen(false)}
            post={post}
          />
        )}
      </>
    );
  }

  // Regular user post
  return (
    <Card className="w-full max-w-2xl mx-auto bg-white shadow-lg rounded-lg overflow-hidden mb-6">
      <CardContent className="p-0">
        {/* Fixed 16:9 Media Container */}
        <div className="relative w-full aspect-video bg-black">
          {(post.imageUrl || post.videoUrl) && (
            <>
              {post.videoUrl ? (
                <div className="relative h-full overflow-hidden">
                  <CachedVideo
                    src={post.videoUrl}
                    className="w-full h-full object-cover cursor-pointer"
                    controls={false}
                    autoPlay={false}
                    muted={true}
                    loop={true}
                  />
                  <div className="absolute inset-0 bg-black bg-opacity-20 opacity-0 hover:opacity-100 transition-opacity flex items-center justify-center pointer-events-none">
                    <div className="text-white pointer-events-auto">
                      {isPlaying ? (
                        isMuted ? <VolumeX className="w-8 h-8" /> : <Volume2 className="w-8 h-8" />
                      ) : (
                        <Play className="w-8 h-8" />
                      )}
                    </div>
                  </div>
                </div>
              ) : (
                <CachedImage
                  src={post.imageUrl}
                  alt="Post content"
                  className="w-full h-full object-cover"
                />
              )}
            </>
          )}
        </div>
      </CardContent>

      {/* Post Details Section - Always Visible */}
      <div className="bg-white p-4 space-y-3 border-t">
        {/* Caption */}
        {post.caption && (
          <div>
            <p className="text-gray-900 text-sm leading-relaxed font-medium">
              {formatContent(post.caption)}
            </p>
            {post.caption.length > 100 && (
              <Button
                variant="ghost"
                size="sm"
                className="p-0 h-auto text-blue-600 hover:text-blue-800 text-xs mt-1"
                onClick={() => setExpandedContent(!expandedContent)}
              >
                {expandedContent ? "Show less" : "Show more"}
              </Button>
            )}
          </div>
        )}

        {/* Interaction Line: Likes, Link, Share */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Button 
              variant="ghost" 
              size="sm" 
              onClick={handleLike}
              disabled={likeMutation.isPending}
              className={cn(
                "flex items-center space-x-1 text-xs transition-colors",
                isLiked 
                  ? "bg-green-100 text-green-700 hover:bg-green-150" 
                  : "hover:bg-green-50 hover:text-green-600"
              )}
            >
              <ThumbsUp className={cn("w-3 h-3", isLiked && "fill-current")} />
              <span>{post.likesCount || 0}</span>
            </Button>
          </div>
          <div className="flex items-center space-x-2">
            {post.link && (
              <Button
                variant="ghost"
                size="sm"
                onClick={() => window.open(post.link, '_blank')}
                className="hover:bg-gray-50 text-xs text-blue-600"
              >
                full details
              </Button>
            )}
            <Button
              variant="ghost"
              size="sm"
              onClick={handleComment}
              className="hover:bg-gray-50"
            >
              <MessageCircle className="w-3 h-3" />
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={handleShare}
              className="hover:bg-gray-50"
            >
              <Share2 className="w-3 h-3" />
            </Button>
            {/* Admin Edit/Delete Buttons for all posts */}
            {currentUser?.isAdmin && (
              <>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={handleEdit}
                  className="hover:bg-blue-50 hover:text-blue-600"
                >
                  <Edit className="w-3 h-3" />
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={handleDelete}
                  disabled={deletePostMutation.isPending}
                  className="hover:bg-red-50 hover:text-red-600"
                >
                  <Trash2 className="w-3 h-3" />
                </Button>
              </>
            )}
          </div>
        </div>
      </div>

      {/* Comment Modal */}
      <CommentModal 
        isOpen={commentModalOpen}
        onClose={() => setCommentModalOpen(false)}
        postId={post.id}
        currentUser={currentUser}
      />

      {/* Edit Modal */}
      {currentUser?.isAdmin && (
        <EditPostModal 
          isOpen={editModalOpen}
          onClose={() => setEditModalOpen(false)}
          post={post}
        />
      )}
    </Card>
  );
}

