server/video-processor.ts.content:
import ffmpeg from 'fluent-ffmpeg';
import ffmpegInstaller from 'ffmpeg-static';
import path from 'path';
import fs from 'fs';
import { posts, vibes, stories } from "@shared/schema";
import { db } from "./db";
import { eq, lt, sql } from "drizzle-orm";

// Set the ffmpeg path
if (ffmpegInstaller) {
  ffmpeg.setFfmpegPath(ffmpegInstaller);
}

/**
 * Trim video to maximum 60 seconds
 */
export async function trimVideo(inputBuffer: Buffer, maxDuration: number = 60): Promise<Buffer> {
  return new Promise((resolve, reject) => {
    const tempInputPath = path.join(process.cwd(), 'uploads', `temp_input_${Date.now()}_${Math.random().toString(36).substring(7)}.mp4`);
    const tempOutputPath = path.join(process.cwd(), 'uploads', `temp_output_${Date.now()}_${Math.random().toString(36).substring(7)}.mp4`);

    console.log(`âœ‚ï¸  Starting FFmpeg trim: ${maxDuration}s max duration`);
    console.log(`ðŸ“ Temp files: ${tempInputPath} -> ${tempOutputPath}`);

    try {
      // Ensure uploads directory exists
      const uploadsDir = path.join(process.cwd(), 'uploads');
      if (!fs.existsSync(uploadsDir)) {
        fs.mkdirSync(uploadsDir, { recursive: true });
      }

      // Write buffer to temporary file
      fs.writeFileSync(tempInputPath, inputBuffer);
      console.log(`ðŸ’¾ Input file written: ${(inputBuffer.length / 1024 / 1024).toFixed(2)}MB`);

      ffmpeg(tempInputPath)
        // CRITICAL: Force exact duration trimming - take only first N seconds
        .seekInput(0) // Start from beginning
        .duration(maxDuration) // Take only maxDuration seconds
        .videoCodec('libx264')
        .audioCodec('aac')
        .format('mp4')
        .outputOptions([
          '-movflags faststart',  // Enable fast start for web playback
          '-preset fast',         // Fast encoding preset
          '-crf 23',              // Good quality compression
          '-avoid_negative_ts make_zero', // Handle timing issues
          '-fflags +genpts'       // Generate presentation timestamps
        ])
        .on('start', (commandLine) => {
          console.log(`ðŸŽ¬ FFmpeg command: ${commandLine}`);
        })
        .on('progress', (progress) => {
          console.log(`â³ Trimming progress: ${progress.percent ? progress.percent.toFixed(1) + '%' : 'processing...'}`);
        })
        .on('end', () => {
          try {
            if (!fs.existsSync(tempOutputPath)) {
              throw new Error(`Output file was not created: ${tempOutputPath}`);
            }

            const outputBuffer = fs.readFileSync(tempOutputPath);
            console.log(`âœ… Video trimmed successfully: ${(outputBuffer.length / 1024 / 1024).toFixed(2)}MB output`);

            // Clean up temporary files
            fs.unlinkSync(tempInputPath);
            fs.unlinkSync(tempOutputPath);

            resolve(outputBuffer);
          } catch (error) {
            console.error('âŒ Error reading processed video:', error);
            reject(error);
          }
        })
        .on('error', (err: any) => {
          console.error('âŒ FFmpeg processing failed:', err);

          // Clean up temporary files
          try {
            if (fs.existsSync(tempInputPath)) fs.unlinkSync(tempInputPath);
            if (fs.existsSync(tempOutputPath)) fs.unlinkSync(tempOutputPath);
          } catch (cleanupError) {
            console.error('Error cleaning up temporary files:', cleanupError);
          }

          reject(new Error(`Video trimming failed: ${err.message || err}`));
        })
        .save(tempOutputPath);
    } catch (error) {
      console.error('âŒ Error setting up video trimming:', error);
      reject(error);
    }
  });
}

/**
 * Get video duration in seconds
 */
export async function getVideoDuration(inputBuffer: Buffer): Promise<number> {
  return new Promise((resolve, reject) => {
    const tempInputPath = path.join(process.cwd(), 'uploads', `temp_duration_${Date.now()}.mp4`);

    try {
      // Write buffer to temporary file
      fs.writeFileSync(tempInputPath, inputBuffer);

      ffmpeg.ffprobe(tempInputPath, (err: any, metadata: any) => {
        // Clean up temporary file
        try {
          fs.unlinkSync(tempInputPath);
        } catch (cleanupError) {
          console.error('Error cleaning up temp file:', cleanupError);
        }

        if (err) {
          console.error('FFprobe error:', err);
          reject(err);
          return;
        }

        const duration = metadata.format.duration || 0;
        resolve(duration);
      });
    } catch (error) {
      console.error('Error in getVideoDuration:', error);
      reject(error);
    }
  });
}

/**
 * Process video: trim if longer than 60 seconds
 */
export async function processVideo(inputBuffer: Buffer): Promise<Buffer> {
  try {
    console.log(`ðŸŽ¬ Processing video (size: ${(inputBuffer.length / 1024 / 1024).toFixed(2)}MB)...`);

    // Get video duration
    const duration = await getVideoDuration(inputBuffer);
    console.log(`ðŸ“ Video duration detected: ${duration} seconds`);

    // ALWAYS trim videos longer than 60 seconds for Play Store compliance
    if (duration > 60) {
      console.log(`âœ‚ï¸  VIDEO TOO LONG: ${duration}s detected - FORCE TRIMMING to first 60s for Play Store compliance`);
      const trimmedBuffer = await trimVideo(inputBuffer, 60);
      console.log(`âœ… Video successfully trimmed from ${duration}s to 60s (new size: ${(trimmedBuffer.length / 1024 / 1024).toFixed(2)}MB)`);
      return trimmedBuffer;
    }

    // If video is 60 seconds or less, return as is
    console.log(`âœ… Video duration OK: ${duration}s is within 60s limit - uploading without changes`);
    return inputBuffer;
  } catch (error) {
    console.error('âŒ CRITICAL ERROR processing video - this might allow long videos to upload:', error);

    // CRITICAL: If duration detection fails, ALWAYS trim to 60s for Play Store compliance
    try {
      console.log('ðŸ”„ CRITICAL FALLBACK: Duration detection failed - FORCE TRIMMING to 60s for Play Store safety...');
      const fallbackTrimmed = await trimVideo(inputBuffer, 60);
      console.log('âœ… Fallback trim successful - video guaranteed to be â‰¤60s');
      return fallbackTrimmed;
    } catch (fallbackError) {
      console.error('âŒ COMPLETE FAILURE: Both duration detection AND trimming failed. This video may violate Play Store policy:', fallbackError);
      // Last resort: still return the original, but this should be investigated
      throw new Error(`Video processing completely failed: ${fallbackError.message}. Upload blocked for Play Store compliance.`);
    }
  }
}

/**
 * Delete videos older than 72 hours from database and Cloudinary
 */
export async function deleteOldVideos(): Promise<void> {
  try {
    const seventyTwoHoursAgo = new Date(Date.now() - 72 * 60 * 60 * 1000);
    console.log(`Deleting videos older than: ${seventyTwoHoursAgo.toISOString()}`);

    // Find old posts with videos
    const oldPosts = await db
      .select()
      .from(posts)
      .where(
        sql`${posts.createdAt} < ${seventyTwoHoursAgo} AND (${posts.videoUrl} IS NOT NULL OR ${posts.video1Url} IS NOT NULL OR ${posts.video2Url} IS NOT NULL OR ${posts.video3Url} IS NOT NULL)`
      );

    console.log(`Found ${oldPosts.length} old posts with videos to delete`);

    // Delete video URLs from posts
    for (const post of oldPosts) {
      const videosToDelete = [];

      if (post.videoUrl) videosToDelete.push(post.videoUrl);
      if (post.video1Url) videosToDelete.push(post.video1Url);
      if (post.video2Url) videosToDelete.push(post.video2Url);
      if (post.video3Url) videosToDelete.push(post.video3Url);

      // Delete videos from Cloudinary
      try {
        const cloudinaryModule = await import('./cloudinary');
        const deleteFromCloudinary = cloudinaryModule.deleteFromCloudinary;
        for (const videoUrl of videosToDelete) {
          await deleteFromCloudinary(videoUrl);
          console.log(`Deleted video from Cloudinary: ${videoUrl}`);
        }
      } catch (cloudinaryError) {
        console.error('Error deleting from Cloudinary:', cloudinaryError);
      }

      // Update post to remove video URLs
      await db
        .update(posts)
        .set({
          videoUrl: null,
          video1Url: null,
          video2Url: null,
          video3Url: null
        })
        .where(eq(posts.id, post.id));

      console.log(`Removed video URLs from post ${post.id}`);
    }

    // Find old vibes with videos
    const oldVibes = await db
      .select()
      .from(vibes)
      .where(
        sql`${vibes.createdAt} < ${seventyTwoHoursAgo} AND ${vibes.videoUrl} IS NOT NULL`
      );

    console.log(`Found ${oldVibes.length} old vibes with videos to delete`);

    // Delete video URLs from vibes
    for (const vibe of oldVibes) {
      if (vibe.videoUrl) {
        try {
          const cloudinaryModule = await import('./cloudinary');
          const deleteFromCloudinary = cloudinaryModule.deleteFromCloudinary;
          await deleteFromCloudinary(vibe.videoUrl);
          console.log(`Deleted vibe video from Cloudinary: ${vibe.videoUrl}`);
        } catch (cloudinaryError) {
          console.error('Error deleting vibe video from Cloudinary:', cloudinaryError);
        }

        // Update vibe to remove video URL
        await db
          .update(vibes)
          .set({ videoUrl: null })
          .where(eq(vibes.id, vibe.id));

        console.log(`Removed video URL from vibe ${vibe.id}`);
      }
    }

    // Find old stories with videos
    const oldStories = await db
      .select()
      .from(stories)
      .where(
        sql`${stories.createdAt} < ${seventyTwoHoursAgo} AND ${stories.videoUrl} IS NOT NULL`
      );

    console.log(`Found ${oldStories.length} old stories with videos to delete`);

    // Delete video URLs from stories
    for (const story of oldStories) {
      if (story.videoUrl) {
        try {
          const cloudinaryModule = await import('./cloudinary');
          const deleteFromCloudinary = cloudinaryModule.deleteFromCloudinary;
          await deleteFromCloudinary(story.videoUrl);
          console.log(`Deleted story video from Cloudinary: ${story.videoUrl}`);
        } catch (cloudinaryError) {
          console.error('Error deleting story video from Cloudinary:', cloudinaryError);
        }

        // Update story to remove video URL
        await db
          .update(stories)
          .set({ videoUrl: null })
          .where(eq(stories.id, story.id));

        console.log(`Removed video URL from story ${story.id}`);
      }
    }

    console.log('Old video deletion completed');
  } catch (error) {
    console.error('Error deleting old videos:', error);
  }
}

/**
 * Start the automatic video cleanup job
 * Runs every 6 hours to delete videos older than 72 hours
 */
export function startVideoCleanupJob(): void {
  // Run immediately on startup
  console.log('ðŸŽ¬ TrendoTalk Video Management System Started');
  console.log('âœ‚ï¸  Auto video trimming: Videos longer than 60 seconds will be automatically trimmed');
  console.log('ðŸ—‘ï¸  Auto cleanup: Videos will be deleted after 72 hours');

  deleteOldVideos();

  // Then run every 6 hours (6 * 60 * 60 * 1000 ms)
  const cleanupInterval = 6 * 60 * 60 * 1000;

  setInterval(() => {
    console.log('ðŸ§¹ Running scheduled video cleanup job...');
    deleteOldVideos();
  }, cleanupInterval);

  console.log('âœ… Video cleanup job started - will run every 6 hours');
}

